import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
// Assuming you have a hamburger icon, for example, from @heroicons/react
// import { Bars3Icon } from '@heroicons/react/24/outline'; // If using Heroicons

const Navbar = ({ onMenuClick }) => { // onMenuClick prop to toggle sidebar on small screens
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  return (
    <header className="
      w-full h-16
      bg-gradient-to-r from-blue-200 to-blue-300
      shadow-md
      flex items-center justify-between
      px-4 sm:px-6 lg:px-8
      text-gray-800
      rounded-md mb-8 lg:mb-0 {/* Added rounded-md and responsive margin */}
    ">
      {/* Hamburger Menu Icon - Visible on small/medium screens, hidden on large */}
      <button
        onClick={onMenuClick} // This prop would be passed from the parent (e.g., Teams) to toggle the sidebar
        className="
          p-2 rounded-md
          focus:outline-none focus:ring-2 focus:ring-blue-500
          lg:hidden {/* Hidden on large screens, assuming sidebar is always visible */}
        "
        aria-label="Toggle menu"
      >
        {/* Replace with your actual hamburger icon SVG or component */}
        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2.5} stroke="currentColor" className="w-7 h-7 text-blue-800">
          <path strokeLinecap="round" strokeLinejoin="round" d="M3.75 6.75h16.5M3.75 12h16.5m-16.5 5.25h16.5" />
        </svg>
      </button>

      {/* This h1 was empty, consider removing or adding a title */}
      <h1 className="text-xl font-semibold hidden sm:block"></h1> {/* Hidden on small screens if no content */}

      {/* Spacer div to push the profile icon to the right on smaller screens where the h1 might be hidden */}
      <div className="flex-grow lg:hidden"></div> {/* Takes up space on smaller screens */}

      <div className="relative flex items-center space-x-4">
        {/* Profile Icon with Dropdown */}
        <div
          className="
            w-10 h-10 bg-gray-100 rounded-full
            flex items-center justify-center
            text-sm font-semibold text-gray-600
            cursor-pointer select-none {/* Added select-none to prevent text selection */}
            shadow-sm hover:shadow-md transition-shadow duration-200
          "
          onClick={() => setDropdownOpen(!dropdownOpen)}
          aria-haspopup="true"
          aria-expanded={dropdownOpen ? "true" : "false"}
        >
          <span>AB</span>
        </div>

        {dropdownOpen && (
          <div className="
            absolute top-12 right-0
            bg-white text-gray-800
            rounded-lg shadow-xl
            w-48 py-2 z-10 {/* Added z-10 to ensure it's on top */}
            transform origin-top-right transition-all duration-200
            scale-100 opacity-100 {/* For potential animation if integrated with a library */}
          ">
            <button
              onClick={handleLogout}
              className="
                block w-full text-left
                px-4 py-2
                hover:bg-gray-100 hover:text-blue-600
                transition duration-200 ease-in-out
              "
            >
              Logout
            </button>
          </div>
        )}
      </div>
    </header>
  );
};

export default Navbar;
