import React, { useState, useEffect } from "react";
import axios from "axios";
import Label from "../components/ui/label"; // Assuming Label.jsx

import Input from "../components/ui/input"; Â  // Assuming Input.jsx

import Button from "../components/ui/button";




const IncidentManagement = () => {
  const [incidents, setIncidents] = useState([]);
  const [formIncident, setFormIncident] = useState({
    title: "",
    description: "",
    status: "investigating",
    services: [],
    type: "incident",
  });
  const [editingIncidentId, setEditingIncidentId] = useState(null);
  const [availableServices, setAvailableServices] = useState([]);
  const [filter, setFilter] = useState("");

  const fetchIncidents = async () => {
    try {
      const res = await axios.get(
        `http://localhost:8001/incidents${filter ? `?days=${filter}` : ""}`
      );
      setIncidents(res.data);
    } catch (error) {
      console.error("Error fetching incidents:", error);
    }
  };

  const fetchAvailableServices = async () => {
    try {
      const response = await axios.get("http://localhost:8001/services/");
      setAvailableServices(response.data);
    } catch (error) {
      console.error("Error fetching available services:", error);
    }
  };

  useEffect(() => {
    fetchIncidents();
    fetchAvailableServices();
  }, [filter]);

  const handleFormChange = (e) => {
    const { name, value, options } = e.target;
    if (name === "services") {
      const selected = Array.from(options)
        .filter((opt) => opt.selected)
        .map((opt) => opt.value);
      setFormIncident((prev) => ({ ...prev, services: selected }));
    } else {
      setFormIncident((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async () => {
    try {
      if (editingIncidentId) {
        const response = await axios.put(
          `http://localhost:8001/incidents/${editingIncidentId}`,
          formIncident
        );
        setIncidents((prev) =>
          prev.map((incident) =>
            incident.id === editingIncidentId ? response.data : incident
          )
        );
        setEditingIncidentId(null);
      } else {
        const response = await axios.post(
          "http://localhost:8001/incidents/",
          formIncident
        );
        setIncidents((prev) => [...prev, response.data]);
      }

      setFormIncident({
        title: "",
        description: "",
        status: "investigating",
        services: [],
        type: "incident",
      });
    } catch (error) {
      console.error(
        "Error submitting incident:",
        error.response ? error.response.data : error.message
      );
      if (error.response?.data?.detail) {
        alert(`Error: ${JSON.stringify(error.response.data.detail)}`);
      }
    }
  };

  const startEditing = (incident) => {
    setEditingIncidentId(incident.id);
    setFormIncident({
      title: incident.title,
      description: incident.description,
      status: incident.status,
      services: incident.services || [],
      type: incident.type,
    });
  };

  const cancelEditing = () => {
    setEditingIncidentId(null);
    setFormIncident({
      title: "",
      description: "",
      status: "investigating",
      services: [],
      type: "incident",
    });
  };

  const deleteIncident = async (id) => {
    try {
      await axios.delete(`http://localhost:8001/incidents/${id}`);
      setIncidents((prev) => prev.filter((incident) => incident.id !== id));
    } catch (error) {
      console.error("Error deleting incident:", error);
    }
  };

  const handleSendAlert = (incident) => {
    const subject = `Incident Alert: ${incident.title}`;
    const body = `An alert has been triggered for the following incident:\n\nTitle: ${incident.title}\nDescription: ${incident.description}\nStatus: ${incident.status}\nType: ${incident.type}`;
    sendEmail("user@example.com", subject, body); // Replace with dynamic email if needed
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6 text-center text-gray-800">
        Incident Management
      </h1>

      {/* Filter */}
      <div className="mb-4">
        <select
          onChange={(e) => setFilter(e.target.value)}
          className="p-2 border rounded"
        >
          <option value="">All Incidents</option>
          <option value="7">Last 7 days</option>
          <option value="30">Last 30 days</option>
        </select>
      </div>

      {/* Form */}
      <div className="bg-white p-6 rounded-lg shadow-xl mb-8 border">
        <h2 className="text-2xl font-semibold mb-4 text-gray-700">
          {editingIncidentId ? "Edit Incident" : "Create New Incident"}
        </h2>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
          <div>
            <Label htmlFor="title">Title</Label>
            <Input
              id="title"
              name="title"
              value={formIncident.title}
              onChange={handleFormChange}
              placeholder="Incident Title"
            />
          </div>
          <div>
            <Label htmlFor="description">Description</Label>
            <Input
              id="description"
              name="description"
              value={formIncident.description}
              onChange={handleFormChange}
              placeholder="Incident Description"
            />
          </div>
          <div>
            <Label htmlFor="status">Status</Label>
            <select
              id="status"
              name="status"
              value={formIncident.status}
              onChange={handleFormChange}
              className="w-full p-2 border rounded"
            >
              <option value="investigating">Investigating</option>
              <option value="identified">Identified</option>
              <option value="monitoring">Monitoring</option>
              <option value="resolved">Resolved</option>
            </select>
          </div>
          <div>
            <Label htmlFor="type">Type</Label>
            <select
              id="type"
              name="type"
              value={formIncident.type}
              onChange={handleFormChange}
              className="w-full p-2 border rounded"
            >
              <option value="incident">Incident</option>
              <option value="maintenance">Maintenance</option>
            </select>
          </div>
          <div>
            <Label htmlFor="services">Affected Services</Label>
            <select
              id="services"
              name="services"
              multiple
              value={formIncident.services}
              onChange={handleFormChange}
              className="w-full p-2 border rounded h-32"
            >
              {availableServices.map((s) => (
                <option key={s.id} value={s.id}>
                  {s.name}
                </option>
              ))}
            </select>
            {availableServices.length === 0 && (
              <p className="text-sm text-gray-500 mt-1">
                No services available. Create some in Service Management first.
              </p>
            )}
          </div>
        </div>

        <div className="flex gap-3 mt-4">
          <Button onClick={handleSubmit}>
            {editingIncidentId ? "Update Incident" : "Create Incident"}
          </Button>
          {editingIncidentId && (
            <Button onClick={cancelEditing} className="bg-gray-500">
              Cancel
            </Button>
          )}
        </div>
      </div>

      {/* Incident List */}
      <div className="bg-white p-6 rounded-lg shadow-xl border">
        <h2 className="text-2xl font-semibold mb-4 text-gray-700">
          Existing Incidents
        </h2>

        {incidents.length === 0 ? (
          <p className="text-center text-gray-500">
            No incidents created yet.
          </p>
        ) : (
          <div className="overflow-x-auto">
            <table className="min-w-full text-left border">
              <thead className="bg-gray-100">
                <tr>
                  <th className="py-2 px-4">Title</th>
                  <th className="py-2 px-4">Description</th>
                  <th className="py-2 px-4">Type</th>
                  <th className="py-2 px-4">Status</th>
                  <th className="py-2 px-4">Services</th>
                  <th className="py-2 px-4">Actions</th>
                </tr>
              </thead>
              <tbody>
                {incidents.map((incident) => (
                  <tr
                    key={incident.id}
                    className="border-t hover:bg-gray-50 transition"
                  >
                    <td className="py-2 px-4">{incident.title}</td>
                    <td className="py-2 px-4">{incident.description}</td>
                    <td className="py-2 px-4">{incident.type}</td>
                    <td className="py-2 px-4">{incident.status}</td>
                    <td className="py-2 px-4">
                      {incident.services?.length > 0
                        ? incident.services
                            .map((id) => {
                              const s = availableServices.find(
                                (svc) => svc.id === id
                              );
                              return s?.name || id;
                            })
                            .join(", ")
                        : "N/A"}
                    </td>
                    <td className="py-2 px-4 flex gap-2 flex-wrap">
                      <Button
                        onClick={() => startEditing(incident)}
                        className="bg-green-600 hover:bg-green-700 text-white"
                      >
                        Edit
                      </Button>
                      <Button
                        onClick={() => deleteIncident(incident.id)}
                        className="bg-red-600 hover:bg-red-700 text-white"
                      >
                        Delete
                      </Button>
                      <Button
                        onClick={() => handleSendAlert(incident)}
                        className="bg-yellow-500 hover:bg-yellow-600 text-white"
                      >
                        Send Alert
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default IncidentManagement;
