import React, { useState, useEffect } from "react";
import axios from "axios";

const OrganizationForm = () => {
  const [orgName, setOrgName] = useState("");
  const [organizations, setOrganizations] = useState([]);

  const fetchOrgs = async () => {
    const res = await axios.get("http://localhost:8001/organizations/");
    setOrganizations(res.data);
  };

  useEffect(() => {
    fetchOrgs();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!orgName) return;
    await axios.post("http://localhost:8001/organizations/", { name: orgName });
    setOrgName("");
    fetchOrgs();
  };

  return (
    <div className="p-8 bg-white shadow-xl rounded-lg max-w-2xl mx-auto mt-8">
      <h2 className="text-2xl font-semibold text-gray-800 mb-6">Create Organization</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div className="flex space-x-4 items-center">
          <input
            type="text"
            placeholder="Enter Organization Name"
            value={orgName}
            onChange={(e) => setOrgName(e.target.value)}
            className="border border-gray-300 rounded-lg px-4 py-2 w-full focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button
            type="submit"
            className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            Create
          </button>
        </div>
      </form>

      <div className="mt-8">
        <h3 className="text-xl font-semibold text-gray-700 mb-4">Your Organizations</h3>
        <div className="bg-gray-50 shadow-md rounded-lg p-4">
          <ul className="space-y-3">
            {organizations.length > 0 ? (
              organizations.map((org) => (
                <li
                  key={org.id}
                  className="text-lg text-gray-800 border-b border-gray-200 pb-2"
                >
                  {org.name}
                </li>
              ))
            ) : (
              <p className="text-gray-500">No organizations found.</p>
            )}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default OrganizationForm;
