import React, { useState, useEffect } from "react";
import axios from "axios";
import Label from "../components/ui/label";
import Input from "../components/ui/input";
import Button from "../components/ui/button";

const IncidentManagement = () => {
  const [incidents, setIncidents] = useState([]);
  const [formIncident, setFormIncident] = useState({
    title: "",
    description: "",
    status: "Resolved",
    serviceId: "", // Assuming incidents are linked to services
  });
  const [editingIncidentId, setEditingIncidentId] = useState(null);

  useEffect(() => {
    fetchIncidents();
  }, []);

  const fetchIncidents = async () => {
    try {
      const response = await axios.get("http://localhost:8001/incidents");
      setIncidents(response.data);
    } catch (error) {
      console.error("Error fetching incidents:", error);
    }
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormIncident((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async () => {
    try {
      if (editingIncidentId) {
        const response = await axios.put(
          `http://localhost:8001/incidents/${editingIncidentId}`,
          formIncident
        );
        setIncidents((prev) =>
          prev.map((incident) =>
            incident.id === editingIncidentId ? response.data : incident
          )
        );
        setEditingIncidentId(null);
      } else {
        const response = await axios.post(
          "http://localhost:8001/incidents",
          formIncident
        );
        setIncidents((prev) => [...prev, response.data]);
      }
      setFormIncident({ title: "", description: "", status: "Resolved", serviceId: "" });
    } catch (error) {
      console.error("Error submitting incident:", error);
    }
  };

  const startEditing = (incident) => {
    setEditingIncidentId(incident.id);
    setFormIncident({
      title: incident.title,
      description: incident.description,
      status: incident.status,
      serviceId: incident.serviceId,
    });
  };

  const cancelEditing = () => {
    setEditingIncidentId(null);
    setFormIncident({ title: "", description: "", status: "Resolved", serviceId: "" });
  };

  const deleteIncident = async (id) => {
    try {
      await axios.delete(`http://localhost:8001/incidents/${id}`);
      setIncidents((prev) => prev.filter((incident) => incident.id !== id));
    } catch (error) {
      console.error("Error deleting incident:", error);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6 text-center">Incident Management</h1>

      {/* New Incident / Edit Incident Form */}
      <div className="bg-white p-6 rounded-lg shadow-md mb-8">
        <h2 className="text-2xl font-semibold mb-4">
          {editingIncidentId ? "Edit Incident" : "Create New Incident"}
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
          <div>
            <Label htmlFor="title" className="block text-gray-700 text-sm font-bold mb-2">Title</Label>
            <Input
              id="title"
              type="text"
              name="title"
              placeholder="Incident Title"
              value={formIncident.title}
              onChange={handleFormChange}
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            />
          </div>
          <div>
            <Label htmlFor="description" className="block text-gray-700 text-sm font-bold mb-2">Description</Label>
            <Input
              id="description"
              type="text"
              name="description"
              placeholder="Incident Description"
              value={formIncident.description}
              onChange={handleFormChange}
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            />
          </div>
          <div>
            <Label htmlFor="status" className="block text-gray-700 text-sm font-bold mb-2">Status</Label>
            <select
              id="status"
              name="status"
              value={formIncident.status}
              onChange={handleFormChange}
              className="shadow border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            >
              <option value="Resolved">Resolved</option>
              <option value="Ongoing">Ongoing</option>
              <option value="Investigation">Investigation</option>
            </select>
          </div>
        </div>
        <div className="flex gap-2">
          <Button
            onClick={handleSubmit}
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          >
            {editingIncidentId ? "Update Incident" : "Create Incident"}
          </Button>
          {editingIncidentId && (
            <Button
              onClick={cancelEditing}
              className="bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            >
              Cancel
            </Button>
          )}
        </div>
      </div>

      {/* Incident List */}
      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-2xl font-semibold mb-4">Existing Incidents</h2>
        {incidents.length === 0 ? (
          <p className="text-gray-600">No incidents created yet.</p>
        ) : (
          <div className="overflow-x-auto">
            <table className="min-w-full bg-white border border-gray-200">
              <thead>
                <tr className="bg-gray-100 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">
                  <th className="py-3 px-4 border-b border-gray-200">Title</th>
                  <th className="py-3 px-4 border-b border-gray-200">Description</th>
                  <th className="py-3 px-4 border-b border-gray-200">Status</th>
                  <th className="py-3 px-4 border-b border-gray-200">Actions</th>
                </tr>
              </thead>
              <tbody>
                {incidents.map((incident) => (
                  <tr key={incident.id} className="hover:bg-gray-50">
                    <td className="py-3 px-4 border-b border-gray-200">{incident.title}</td>
                    <td className="py-3 px-4 border-b border-gray-200">{incident.description}</td>
                    <td className="py-3 px-4 border-b border-gray-200">{incident.status}</td>
                    <td className="py-3 px-4 border-b border-gray-200 flex gap-2">
                      <Button
                        onClick={() => startEditing(incident)}
                        className="bg-green-500 hover:bg-green-700 text-white text-sm py-1 px-3 rounded"
                      >
                        Edit
                      </Button>
                      <Button
                        onClick={() => deleteIncident(incident.id)}
                        className="bg-red-500 hover:bg-red-700 text-white text-sm py-1 px-3 rounded"
                      >
                        Delete
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default IncidentManagement;
