import React from "react";
import { useHistory } from "react-router-dom";
import { useAuth0 } from "@auth0/auth0-react"; // If using Auth0 for authentication

const Navbar = () => {
  const { logout } = useAuth0(); // Use the Auth0 logout hook
  const history = useHistory();

  const handleLogout = () => {
    logout({ returnTo: window.location.origin }); // Logs out the user and redirects them to the homepage or specified URL
    history.push("/"); // Redirect to the login page after logout
  };

  return (
    <header className="ml-64 h-16 bg-white shadow-sm flex items-center justify-between px-6">
      <h1 className="text-xl font-semibold text-gray-800">Dashboard</h1>
      <div className="flex items-center space-x-4">
        <button className="text-gray-500 hover:text-gray-700">ðŸ””</button>
        <div className="w-8 h-8 bg-gray-200 rounded-full" />
        <button
          onClick={handleLogout}
          className="text-gray-500 hover:text-gray-700"
        >
          Logout
        </button>
      </div>
    </header>
  );
};

export default Navbar;
