import React, { useState, useEffect } from "react";
import axios from "axios";
import Label from "../components/ui/label"; // Assuming Label.jsx

import Input from "../components/ui/input";   // Assuming Input.jsx

import Button from "../components/ui/button";
import { Textarea } from "../components/ui/textarea";
import Select from 'react-select';

import emailjs from "emailjs-com";

import toast from "react-hot-toast";

const options = [
  { value: "Operational", label: "Operational" },
  { value: "Degraded", label: "Degraded" },
  { value: "Down", label: "Down" },
];

const IncidentManagement = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [status, setStatus] = useState("");
  const [incidentId, setIncidentId] = useState(null);
  const [incidents, setIncidents] = useState([]);
  const [services, setServices] = useState([]);
  const [affectedServices, setAffectedServices] = useState([]);

  useEffect(() => {
    fetchIncidents();
    fetchServices();
  }, []);

  const fetchServices = async () => {
    try {
      const response = await axios.get("/api/services");
      const serviceOptions = response.data.map((service) => ({
        value: service.id,
        label: service.name,
      }));
      setServices(serviceOptions);
    } catch (error) {
      console.error("Error fetching services", error);
    }
  };

  const fetchIncidents = async () => {
    try {
      const response = await axios.get("/api/incidents");
      setIncidents(response.data);
    } catch (error) {
      console.error("Error fetching incidents", error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const payload = {
      title,
      description,
      status,
      affected_service_ids: affectedServices.map((s) => s.value),
    };

    try {
      if (incidentId) {
        await axios.put(`/api/incidents/${incidentId}`, payload);
        toast.success("Incident updated");
      } else {
        await axios.post("/api/incidents", payload);
        toast.success("Incident created");
      }
      // Reset form
      setTitle("");
      setDescription("");
      setStatus("");
      setAffectedServices([]);
      setIncidentId(null);
      fetchIncidents();
    } catch (error) {
      toast.error("Something went wrong");
      console.error(error);
    }
  };

  const handleEdit = (incident) => {
    setTitle(incident.title);
    setDescription(incident.description);
    setStatus(incident.status);
    setIncidentId(incident.id);
    setAffectedServices(
      incident.affected_services.map((s) => ({
        value: s.id,
        label: s.name,
      }))
    );
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/incidents/${id}`);
      toast.success("Incident deleted");
      fetchIncidents();
    } catch (error) {
      toast.error("Something went wrong");
      console.error(error);
    }
  };

  return (
    <div className="p-8 max-w-screen-xl mx-auto space-y-10">
      <h2 className="text-3xl font-bold text-center">Incident Management</h2>

      <form
        onSubmit={handleSubmit}
        className="bg-white p-6 rounded-lg shadow-md space-y-6"
      >
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <Label>Title</Label>
            <Input
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              placeholder="Incident Title"
              required
            />
          </div>
          <div>
            <Label>Status</Label>
            <Select
              value={options.find((option) => option.value === status)}
              onChange={(selected) => setStatus(selected.value)}
              options={options}
              placeholder="Select Status"
              className="react-select-container"
              classNamePrefix="react-select"
            />
          </div>
          <div className="md:col-span-2">
            <Label>Description</Label>
            <Textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Incident Description"
              rows={4}
              required
            />
          </div>
          <div className="md:col-span-2">
            <Label>Affected Services</Label>
            <Select
              isMulti
              value={affectedServices}
              onChange={setAffectedServices}
              options={services}
              placeholder="Select Services"
              className="react-select-container"
              classNamePrefix="react-select"
            />
          </div>
        </div>
        <div className="flex justify-end">
          <Button type="submit">{incidentId ? "Update" : "Create"} Incident</Button>
        </div>
      </form>

      <div className="overflow-x-auto bg-white shadow-md rounded-lg">
        <table className="min-w-full text-sm text-left border">
          <thead className="bg-gray-100 text-gray-600 uppercase text-xs">
            <tr>
              <th className="px-4 py-3 border">Title</th>
              <th className="px-4 py-3 border">Status</th>
              <th className="px-4 py-3 border">Services</th>
              <th className="px-4 py-3 border">Actions</th>
            </tr>
          </thead>
          <tbody>
            {incidents.map((incident, index) => (
              <tr
                key={incident.id}
                className={index % 2 === 0 ? "bg-white" : "bg-gray-50"}
              >
                <td className="px-4 py-3 border">{incident.title}</td>
                <td className="px-4 py-3 border">{incident.status}</td>
                <td className="px-4 py-3 border">
                  {incident.affected_services.map((s) => s.name).join(", ")}
                </td>
                <td className="px-4 py-3 border space-x-2">
                  <Button variant="outline" onClick={() => handleEdit(incident)}>
                    Edit
                  </Button>
                  <Button
                    variant="destructive"
                    onClick={() => handleDelete(incident.id)}
                  >
                    Delete
                  </Button>
                </td>
              </tr>
            ))}
            {incidents.length === 0 && (
              <tr>
                <td colSpan="4" className="text-center py-6 text-gray-500">
                  No incidents found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default IncidentManagement;
