import React, { useState, useEffect } from "react";
import axios from "axios";

const OrganizationForm = () => {
  const [orgName, setOrgName] = useState("");
  const [organizations, setOrganizations] = useState([]);

  const fetchOrgs = async () => {
    const res = await axios.get("http://localhost:8001/organizations/");
    setOrganizations(res.data);
  };

  useEffect(() => {
    fetchOrgs();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!orgName) return;
    await axios.post("http://localhost:8001/organizations/", { name: orgName });
    setOrgName("");
    fetchOrgs();
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-2">Create Organization</h2>
      <form onSubmit={handleSubmit} className="mb-4">
        <input
          type="text"
          placeholder="Org Name"
          value={orgName}
          onChange={(e) => setOrgName(e.target.value)}
          className="border px-2 py-1 mr-2"
        />
        <button className="bg-blue-500 text-white px-3 py-1 rounded">Create</button>
      </form>

      <h3 className="text-lg font-semibold">Your Organizations</h3>
      <ul className="list-disc ml-5 mt-2">
        {organizations.map((org) => (
          <li key={org.id}>{org.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default OrganizationForm;
