import React, { useState, useEffect } from "react";
import axios from "axios";
import Label from "../components/ui/label"; // Assuming Label.jsx

import Input from "../components/ui/input";   // Assuming Input.jsx

import Button from "../components/ui/button";
import { Textarea } from "../components/ui/textarea";
import Select from 'react-select';

import emailjs from "emailjs-com";



const IncidentManagement = () => {
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    status: "",
    affectedServices: [],
    startedAt: "",
    resolvedAt: "",
  });
  const [incidents, setIncidents] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [services, setServices] = useState([]);

  const statusOptions = [
    { value: "investigating", label: "Investigating" },
    { value: "identified", label: "Identified" },
    { value: "monitoring", label: "Monitoring" },
    { value: "resolved", label: "Resolved" },
  ];

  const fetchIncidents = async () => {
    const res = await axios.get("/api/incidents");
    setIncidents(res.data);
  };

  const fetchServices = async () => {
    const res = await axios.get("/api/services");
    setServices(res.data.map((svc) => ({ value: svc.id, label: svc.name })));
  };

  useEffect(() => {
    fetchIncidents();
    fetchServices();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const payload = {
      ...formData,
      affectedServices: formData.affectedServices.map((s) => s.value),
    };

    if (editingId) {
      await axios.put(`/api/incidents/${editingId}`, payload);
    } else {
      await axios.post("/api/incidents", payload);
    }

    fetchIncidents();
    setFormData({
      title: "",
      description: "",
      status: "",
      affectedServices: [],
      startedAt: "",
      resolvedAt: "",
    });
    setEditingId(null);
  };

  const handleEdit = (incident) => {
    setFormData({
      title: incident.title,
      description: incident.description,
      status: incident.status,
      affectedServices: services.filter((s) =>
        incident.affectedServices.includes(s.value)
      ),
      startedAt: incident.startedAt,
      resolvedAt: incident.resolvedAt,
    });
    setEditingId(incident.id);
  };

  const handleDelete = async (id) => {
    await axios.delete(`/api/incidents/${id}`);
    fetchIncidents();
  };

  return (
    <div className="p-8 max-w-7xl mx-auto">
      <h1 className="text-3xl font-bold mb-8 text-center">
        Incident Management
      </h1>

      {/* Incident Form */}
      <form
        onSubmit={handleSubmit}
        className="bg-white p-6 rounded-lg shadow-md space-y-6"
      >
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label className="block text-sm font-medium mb-1">Title</label>
            <input
              type="text"
              className="w-full border border-gray-300 rounded-md px-4 py-2"
              value={formData.title}
              onChange={(e) =>
                setFormData({ ...formData, title: e.target.value })
              }
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">Status</label>
            <select
              className="w-full border border-gray-300 rounded-md px-4 py-2"
              value={formData.status}
              onChange={(e) =>
                setFormData({ ...formData, status: e.target.value })
              }
              required
            >
              <option value="">Select Status</option>
              {statusOptions.map((opt) => (
                <option key={opt.value} value={opt.value}>
                  {opt.label}
                </option>
              ))}
            </select>
          </div>
          <div className="md:col-span-2">
            <label className="block text-sm font-medium mb-1">
              Description
            </label>
            <textarea
              className="w-full border border-gray-300 rounded-md px-4 py-2"
              value={formData.description}
              onChange={(e) =>
                setFormData({ ...formData, description: e.target.value })
              }
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">
              Affected Services
            </label>
            <Select
              isMulti
              options={services}
              value={formData.affectedServices}
              onChange={(selected) =>
                setFormData({ ...formData, affectedServices: selected })
              }
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">Started At</label>
            <input
              type="datetime-local"
              className="w-full border border-gray-300 rounded-md px-4 py-2"
              value={formData.startedAt}
              onChange={(e) =>
                setFormData({ ...formData, startedAt: e.target.value })
              }
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">Resolved At</label>
            <input
              type="datetime-local"
              className="w-full border border-gray-300 rounded-md px-4 py-2"
              value={formData.resolvedAt}
              onChange={(e) =>
                setFormData({ ...formData, resolvedAt: e.target.value })
              }
            />
          </div>
        </div>
        <div className="text-right">
          <button
            type="submit"
            className="bg-blue-600 text-white px-6 py-2 rounded-md hover:bg-blue-700"
          >
            {editingId ? "Update Incident" : "Create Incident"}
          </button>
        </div>
      </form>

      {/* Incident Table */}
      <div className="mt-12">
        <h2 className="text-2xl font-semibold mb-4">All Incidents</h2>
        <div className="overflow-x-auto rounded-lg shadow-md">
          <table className="min-w-full bg-white text-sm text-left border border-gray-200">
            <thead className="bg-gray-100 text-gray-700">
              <tr>
                <th className="px-4 py-2">Title</th>
                <th className="px-4 py-2">Status</th>
                <th className="px-4 py-2">Services</th>
                <th className="px-4 py-2">Started</th>
                <th className="px-4 py-2">Resolved</th>
                <th className="px-4 py-2 text-right">Actions</th>
              </tr>
            </thead>
            <tbody>
              {incidents.map((incident, idx) => (
                <tr
                  key={incident.id}
                  className={idx % 2 === 0 ? "bg-white" : "bg-gray-50"}
                >
                  <td className="px-4 py-2 font-medium">{incident.title}</td>
                  <td className="px-4 py-2 capitalize">{incident.status}</td>
                  <td className="px-4 py-2">
                    {incident.affectedServices.join(", ")}
                  </td>
                  <td className="px-4 py-2">
                    {new Date(incident.startedAt).toLocaleString()}
                  </td>
                  <td className="px-4 py-2">
                    {incident.resolvedAt
                      ? new Date(incident.resolvedAt).toLocaleString()
                      : "-"}
                  </td>
                  <td className="px-4 py-2 text-right space-x-2">
                    <button
                      onClick={() => handleEdit(incident)}
                      className="text-blue-600 hover:underline"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete(incident.id)}
                      className="text-red-600 hover:underline"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
              {incidents.length === 0 && (
                <tr>
                  <td colSpan="6" className="text-center py-6 text-gray-500">
                    No incidents found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default IncidentManagement;
