import { useState, useEffect } from 'react';
import UptimeGraph from '../components/UptimeGraph';
import axios from 'axios';


// While not directly used for class names anymore, keeping this as a reference
// for understanding status meanings. Tailwind's JIT mode handles arbitrary values.
const statusMeaning = {
  operational: "green",
  degraded: "orange",
  "partial outage": "orange",
  "major outage": "red",
};

const Dashboard = () => {
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:8001/services/')
      .then((res) => {
        setServices(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching services:", err);
        setError("Failed to load services. Please try again later.");
        setLoading(false);
      });
  }, []);

  return (
    // Main container for the whole dashboard page
    // Added a subtle gray background and overall padding
    <div className="min-h-screen bg-gray-100 p-4 sm:p-6 lg:p-8 font-sans">
      {/* Page Title */}
      <h1 className="text-4xl sm:text-5xl font-extrabold text-center text-gray-800 mb-10 tracking-tight">
        System Dashboard
      </h1>

      {/* Loading and Error States */}
      {loading && (
        <div className="text-center text-gray-600 text-lg py-10">Loading dashboard data...</div>
      )}
      {error && (
        <div className="text-center text-red-600 text-lg py-10 bg-red-50 rounded-lg shadow-sm mx-auto max-w-2xl">
          {error}
        </div>
      )}

      {!loading && !error && (
        <>
          {/* Service Uptime Overview Card */}
          {/* Added consistent padding, rounded corners, and shadow for a card-like appearance */}
          <div className="bg-white p-6 sm:p-8 rounded-xl shadow-lg mb-10 max-w-3xl mx-auto">
            {/* The UptimeGraph component itself is already designed to be visually appealing */}
            {services.length > 0 ? (
              <UptimeGraph serviceData={services} />
            ) : (
              <div className="text-center text-gray-500 py-10 text-lg">
                No service data available. Please add services in Service Management.
              </div>
            )}
          </div>

          {/* Services Overview Section */}
          <h2 className="text-3xl sm:text-4xl font-bold text-gray-800 mb-6 text-center sm:text-left tracking-tight">
            Services Overview
          </h2>
          
          {/* Services List Card */}
          {/* Added consistent padding, rounded corners, and shadow for a card-like appearance */}
          {services.length === 0 ? (
            <div className="bg-white p-6 sm:p-8 rounded-xl shadow-lg text-center text-gray-500 py-8 text-lg max-w-3xl mx-auto">
              No services configured yet.
            </div>
          ) : (
            <ul className="bg-white p-6 sm:p-8 rounded-xl shadow-lg divide-y divide-gray-200 max-w-3xl mx-auto"> {/* Added subtle dividers */}
              {services.map((service) => (
                <li key={service.id} className="flex items-center justify-between py-4"> {/* Increased vertical padding */}
                  <span className="text-lg font-medium text-gray-900">{service.name}</span>
                  {/* Enhanced status pill styling with dynamic colors and rounded shape */}
                  <span className={`px-4 py-1.5 rounded-full text-sm font-semibold capitalize transition-colors duration-200
                    ${service.status === 'Operational' ? 'bg-green-100 text-green-800' : ''}
                    ${service.status === 'Degraded' || service.status === 'Partial Outage' ? 'bg-yellow-100 text-yellow-800' : ''} {/* Changed orange to yellow for better contrast/standard */}
                    ${service.status === 'Major Outage' ? 'bg-red-100 text-red-800' : ''}
                  `}>
                    {service.status}
                  </span>
                </li>
              ))}
            </ul>
          )}
        </>
      )}

      {/* Font Import - Ensure 'Inter' is loaded for a modern look */}
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700;800&display=swap');
        body { font-family: 'Inter', sans-serif; }
      `}</style>
    </div>
  );
};

export default Dashboard;
