import React, { useEffect, useState } from "react";
import axios from "axios";

const IncidentTimeline = () => {
  const [incidents, setIncidents] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8001/incidents/")
      .then((res) => setIncidents(res.data));
  }, []);

  return (
    <div className="mt-6">
      <h2 className="text-lg font-semibold mb-2">Recent Incidents</h2>
      {incidents.length === 0 ? (
        <p>No incidents reported.</p>
      ) : (
        incidents.slice().reverse().map((incident) => (
          <div key={incident.id} className="border p-3 mb-4 rounded">
            <h3 className="font-bold">{incident.title}</h3>
            <p className="text-sm text-gray-500">{new Date(incident.created_at).toLocaleString()}</p>
            <p className="mt-1"><strong>Status:</strong> {incident.status}</p>
            <p className="mt-1"><strong>Type:</strong> {incident.type}</p>
            <p className="mt-1 whitespace-pre-line">{incident.description}</p>
          </div>
        ))
      )}
    </div>
  );
};

export default IncidentTimeline;
