import React, { useState, useEffect } from "react";
import axios from "axios";
// Correct default imports and casing for your UI components
import Label from "../components/ui/Label"; // Assuming Label.jsx
import Input from "../components/ui/Input";   // Assuming Input.jsx
import Button from "../components/ui/Button"; // Assuming Button.jsx

const IncidentManagement = () => {
  const [incidents, setIncidents] = useState([]);
  // Initialize formIncident with all fields expected by the backend
  const [formIncident, setFormIncident] = useState({
    title: "",
    description: "",
    status: "investigating", // Changed default to 'investigating' for incidents
    services: [], // This must be an array of service IDs
    type: "incident", // Default type
  });
  const [editingIncidentId, setEditingIncidentId] = useState(null);
  const [availableServices, setAvailableServices] = useState([]); // To populate service dropdown

  useEffect(() => {
    fetchIncidents();
    fetchAvailableServices(); // Fetch services to link to incidents
  }, []);

  const fetchIncidents = async () => {
    try {
      // Use trailing slash for GET as well for consistency, or remove in backend
      const response = await axios.get("http://localhost:8001/incidents/");
      setIncidents(response.data);
    } catch (error) {
      console.error("Error fetching incidents:", error);
    }
  };

  // Fetch available services from your /services endpoint
  const fetchAvailableServices = async () => {
    try {
      const response = await axios.get("http://localhost:8001/services/");
      setAvailableServices(response.data);
    } catch (error) {
      console.error("Error fetching available services:", error);
    }
  };

  const handleFormChange = (e) => {
    const { name, value, options } = e.target;

    // Special handling for the 'services' multi-select
    if (name === "services") {
      const selectedServices = Array.from(options)
        .filter(option => option.selected)
        .map(option => option.value);
      setFormIncident((prev) => ({ ...prev, services: selectedServices }));
    } else {
      setFormIncident((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async () => {
    try {
      if (editingIncidentId) {
        // Update operation (your backend PUT expects IncidentUpdate)
        // Adjust this part based on your backend's PUT endpoint
        // Current backend PUT expects 'status' and 'update_message'.
        // If you want to update all fields, you need a different PUT model/endpoint.
        // For now, I'll make a simple update for status and add a placeholder update_message
        const updateData = {
            status: formIncident.status,
            update_message: "Updated via UI.", // Placeholder message
        };
        const response = await axios.put(
          `http://localhost:8001/incidents/${editingIncidentId}`,
          updateData
        );
        // Assuming backend PUT returns the updated incident or a success message
        // If backend returns just {"detail": "Updated"}, you'll need to re-fetch
        fetchIncidents(); // Re-fetch to get the latest state after update
        setEditingIncidentId(null);
      } else {
        // Create operation
        const response = await axios.post("http://localhost:8001/incidents/", formIncident); // Added trailing slash
        setIncidents((prev) => [...prev, response.data]);
      }
      // Reset form after create/update
      setFormIncident({
        title: "",
        description: "",
        status: "investigating",
        services: [],
        type: "incident",
      });
    } catch (error) {
      console.error("Error submitting incident:", error.response ? error.response.data : error.message);
      // Display specific error to user if needed
      if (error.response && error.response.data && error.response.data.detail) {
        alert(`Error: ${JSON.stringify(error.response.data.detail)}`);
      }
    }
  };

  const startEditing = (incident) => {
    setEditingIncidentId(incident.id);
    setFormIncident({
      title: incident.title,
      description: incident.description,
      status: incident.status,
      services: incident.services || [], // Ensure services is an array
      type: incident.type,
    });
  };

  const cancelEditing = () => {
    setEditingIncidentId(null);
    setFormIncident({ title: "", description: "", status: "investigating", services: [], type: "incident" });
  };

  const deleteIncident = async (id) => {
    try {
      await axios.delete(`http://localhost:8001/incidents/${id}`); // No trailing slash for DELETE typically
      setIncidents((prev) => prev.filter((incident) => incident.id !== id));
    } catch (error) {
      console.error("Error deleting incident:", error);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6 text-center text-gray-800">Incident Management</h1>

      {/* New Incident / Edit Incident Form */}
      <div className="bg-white p-6 rounded-lg shadow-xl mb-8 border border-gray-200">
        <h2 className="text-2xl font-semibold mb-4 text-gray-700">
          {editingIncidentId ? "Edit Incident" : "Create New Incident"}
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
          {/* Title Field */}
          <div>
            <Label htmlFor="title" className="block text-gray-700 text-sm font-bold mb-2">Title</Label>
            <Input
              id="title"
              type="text"
              name="title"
              placeholder="Incident Title"
              value={formIncident.title}
              onChange={handleFormChange}
            />
          </div>
          {/* Description Field */}
          <div>
            <Label htmlFor="description" className="block text-gray-700 text-sm font-bold mb-2">Description</Label>
            <Input
              id="description"
              type="text"
              name="description"
              placeholder="Incident Description"
              value={formIncident.description}
              onChange={handleFormChange}
            />
          </div>
          {/* Status Select */}
          <div>
            <Label htmlFor="status" className="block text-gray-700 text-sm font-bold mb-2">Status</Label>
            <select
              id="status"
              name="status"
              value={formIncident.status}
              onChange={handleFormChange}
              className="shadow border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            >
              <option value="investigating">Investigating</option>
              <option value="identified">Identified</option>
              <option value="monitoring">Monitoring</option>
              <option value="resolved">Resolved</option>
            </select>
          </div>
          {/* Type Select */}
          <div>
            <Label htmlFor="type" className="block text-gray-700 text-sm font-bold mb-2">Type</Label>
            <select
              id="type"
              name="type"
              value={formIncident.type}
              onChange={handleFormChange}
              className="shadow border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            >
              <option value="incident">Incident</option>
              <option value="maintenance">Maintenance</option>
            </select>
          </div>
          {/* Services Multi-Select */}
          <div>
            <Label htmlFor="services" className="block text-gray-700 text-sm font-bold mb-2">Affected Services</Label>
            <select
              id="services"
              name="services"
              multiple // Enable multi-selection
              value={formIncident.services}
              onChange={handleFormChange}
              className="shadow border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline h-32" // Added height for multi-select
            >
              {availableServices.map((service) => (
                <option key={service.id} value={service.id}>
                  {service.name}
                </option>
              ))}
            </select>
            {availableServices.length === 0 && (
                <p className="text-sm text-gray-500 mt-1">No services available. Create some in Service Management first.</p>
            )}
          </div>
        </div>
        <div className="flex gap-3 mt-4">
          <Button
            onClick={handleSubmit}
            className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg focus:outline-none focus:shadow-outline transition duration-200 ease-in-out"
          >
            {editingIncidentId ? "Update Incident" : "Create Incident"}
          </Button>
          {editingIncidentId && (
            <Button
              onClick={cancelEditing}
              className="bg-gray-500 hover:bg-gray-600 text-white font-bold py-2 px-4 rounded-lg focus:outline-none focus:shadow-outline transition duration-200 ease-in-out"
            >
              Cancel
            </Button>
          )}
        </div>
      </div>

      {/* Incident List */}
      <div className="bg-white p-6 rounded-lg shadow-xl border border-gray-200">
        <h2 className="text-2xl font-semibold mb-4 text-gray-700">Existing Incidents</h2>
        {incidents.length === 0 ? (
          <p className="text-gray-600 text-center py-4">No incidents created yet. Start by creating one above!</p>
        ) : (
          <div className="overflow-x-auto">
            <table className="min-w-full bg-white border border-gray-200 rounded-lg overflow-hidden">
              <thead>
                <tr className="bg-gray-100 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">
                  <th className="py-3 px-4 border-b border-gray-200">Title</th>
                  <th className="py-3 px-4 border-b border-gray-200">Description</th>
                  <th className="py-3 px-4 border-b border-gray-200">Type</th>
                  <th className="py-3 px-4 border-b border-gray-200">Status</th>
                  <th className="py-3 px-4 border-b border-gray-200">Services</th>
                  <th className="py-3 px-4 border-b border-gray-200">Actions</th>
                </tr>
              </thead>
              <tbody>
                {incidents.map((incident) => (
                  <tr key={incident.id} className="border-b border-gray-200 last:border-b-0 hover:bg-gray-50 transition duration-150 ease-in-out">
                    <td className="py-3 px-4">{incident.title}</td>
                    <td className="py-3 px-4">{incident.description}</td>
                    <td className="py-3 px-4">{incident.type}</td>
                    <td className="py-3 px-4">{incident.status}</td>
                    <td className="py-3 px-4">
                      {incident.services && incident.services.length > 0
                        ? incident.services.join(", ")
                        : "N/A"}
                    </td>
                    <td className="py-3 px-4 flex gap-2">
                      <Button
                        onClick={() => startEditing(incident)}
                        className="bg-green-600 hover:bg-green-700 text-white text-sm py-1 px-3 rounded-md transition duration-200 ease-in-out"
                      >
                        Edit
                      </Button>
                      <Button
                        onClick={() => deleteIncident(incident.id)}
                        className="bg-red-600 hover:bg-red-700 text-white text-sm py-1 px-3 rounded-md transition duration-200 ease-in-out"
                      >
                        Delete
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default IncidentManagement;
