import { useState, useEffect } from 'react';
import UptimeGraph from '../components/UptimeGraph';
import axios from 'axios';

const statusColor = {
  operational: "green",
  degraded: "orange",
  down: "red",
};

const Dashboard = () => {
  const [services, setServices] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:8001/services/')
      .then((res) => setServices(res.data));
  }, []);

  // Prepare data for the uptime graph
  const prepareUptimeData = (serviceData) => {
    return serviceData.map(service => ({
      status: service.status,
      timestamp: service.timestamp,
    }));
  };

  return (
    <div>
      <h1 className="text-xl font-bold">Dashboard</h1>
      {services.length > 0 && <UptimeGraph serviceData={prepareUptimeData(services)} />}
      
      <h2 className="text-lg font-semibold mb-2">Services</h2>
      <ul className="space-y-2">
        {services.map((service) => (
          <li key={service.id} className="flex items-center justify-between border p-2 rounded">
            <span>{service.name}</span>
            <span className={`text-${statusColor[service.status]}-600 font-bold capitalize`}>
              {service.status}
            </span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Dashboard;
