import React, { useContext, useEffect, useState } from 'react';
import AuthContext from '../contexts/AuthContext';
import axios from 'axios';

const Dashboard = () => {
    const { user, loading: userLoading } = useContext(AuthContext);
    const [savedFeeds, setSavedFeeds] = useState([]);
    const [feedsLoading, setFeedsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchSavedFeeds = async () => {
            if (!user) {
                setFeedsLoading(false);
                setSavedFeeds([]);
                return;
            }

            try {
                setFeedsLoading(true);
                setError(null);
                
                const response = await axios.get('http://localhost:5000/api/feed/saved', {
                    headers: { 
                        Authorization: `Bearer ${localStorage.getItem('token')}` 
                    },
                });

                // Ensure we have an array of feeds
                const feeds = Array.isArray(response.data) ? response.data : [];
                setSavedFeeds(feeds);
            } catch (err) {
                console.error("Error fetching saved feeds:", err);
                setError("Failed to load saved feeds. Please try again.");
                setSavedFeeds([]);
            } finally {
                setFeedsLoading(false);
            }
        };

        fetchSavedFeeds();
    }, [user]); // Re-fetch when user changes

    if (userLoading) {
        return <div>Loading user data...</div>;
    }

    if (!user) {
        return <div>Please log in to view your dashboard.</div>;
    }

    return (
        <div className="bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-xl font-bold mb-4">Dashboard</h2>
            <div className="mb-6">
                <h3 className="text-lg font-semibold">Welcome, {user.name || user.email}</h3>
                <p className="text-lg">Credits: {user.credits || 0}</p>
            </div>

            <div className="mb-6">
                <h3 className="text-lg font-semibold mb-2">Saved Feeds</h3>
                {feedsLoading ? (
                    <div>Loading saved feeds...</div>
                ) : error ? (
                    <p className="text-red-500">{error}</p>
                ) : savedFeeds.length > 0 ? (
                    <ul className="space-y-3">
                        {savedFeeds.map((feed) => (
                            <li key={feed._id} className="p-3 border rounded-lg hover:bg-gray-50">
                                <a
                                    href={feed.url}
                                    target="_blank"
                                    rel="noopener noreferrer"
                                    className="text-blue-600 hover:underline font-medium"
                                >
                                    {feed.title}
                                </a>
                                <div className="flex justify-between mt-1 text-sm text-gray-500">
                                    <span>Source: {feed.source}</span>
                                    <span>{new Date(feed.createdAt).toLocaleDateString()}</span>
                                </div>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p className="text-gray-500">No saved feeds available.</p>
                )}
            </div>

            <div>
                <h3 className="text-lg font-semibold mb-2">Recent Activity</h3>
                <ul className="space-y-1">
                    <li>Last Login: {user.lastLogin ? new Date(user.lastLogin).toLocaleString() : 'Unknown'}</li>
                    <li>Profile Completed: {user.profileCompleted ? 'Yes' : 'No'}</li>
                </ul>
            </div>
        </div>
    );
};

export default Dashboard;
