import { useState, useEffect } from 'react';
import UptimeGraph from '../components/UptimeGraph';
import axios from 'axios';


const Dashboard = () => {
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:8001/services/')
      .then((res) => {
        setServices(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching services:", err);
        setError("Failed to load services. Please try again later.");
        setLoading(false);
      });
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 via-white to-gray-100 px-4 py-8 sm:px-8 lg:px-16 font-sans">
      <h1 className="text-4xl font-extrabold text-center text-gray-800 mb-12">
        ðŸš¦ System Status Dashboard
      </h1>

      {loading && (
        <div className="text-center text-gray-600 text-lg animate-pulse">Loading dashboard data...</div>
      )}

      {error && (
        <div className="bg-red-100 text-red-800 text-center p-4 rounded-lg shadow-md max-w-2xl mx-auto">
          {error}
        </div>
      )}

      {!loading && !error && (
        <>
          {/* Uptime Graph Section */}
          <div className="bg-white p-6 rounded-2xl shadow-xl mb-12 max-w-4xl mx-auto border border-gray-100 hover:shadow-2xl transition-shadow">
            {services.length > 0 ? (
              <UptimeGraph serviceData={services} />
            ) : (
              <div className="text-center text-gray-500 py-10 text-lg">
                No service data available. Add services in Service Management.
              </div>
            )}
          </div>

          {/* Services Overview */}
          <h2 className="text-3xl font-bold text-gray-800 mb-6 text-center">
            ðŸ§© Services Overview
          </h2>

          {services.length === 0 ? (
            <div className="bg-white p-6 rounded-xl shadow-lg text-center text-gray-500 py-8 text-lg max-w-3xl mx-auto">
              No services configured yet.
            </div>
          ) : (
            <ul className="bg-white p-6 rounded-xl shadow-md divide-y divide-gray-100 max-w-3xl mx-auto border border-gray-100">
              {services.map((service) => (
                <li key={service.id} className="flex items-center justify-between py-4 transition-all hover:bg-gray-50 rounded-lg px-2">
                  <span className="text-lg font-medium text-gray-900">{service.name}</span>
                  <span
                    className={`px-3 py-1 rounded-full text-sm font-semibold capitalize transition
                      ${service.status === 'Operational' ? 'bg-green-100 text-green-700' : ''}
                      ${service.status === 'Degraded' || service.status === 'Partial Outage' ? 'bg-yellow-100 text-yellow-700' : ''}
                      ${service.status === 'Major Outage' ? 'bg-red-100 text-red-700' : ''}
                    `}
                  >
                    {service.status}
                  </span>
                </li>
              ))}
            </ul>
          )}
        </>
      )}
    </div>
  );
};

export default Dashboard;
