import { useState, useEffect } from 'react';
import UptimeGraph from '../components/UptimeGraph';
import axios from 'axios';


// Status meaning definitions
const statusMeaning = {
  operational: "green",
  degraded: "orange",
  "partial outage": "orange",
  "major outage": "red",
};

const Dashboard = () => {
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:8001/services/')
      .then((res) => {
        setServices(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching services:", err);
        setError("Failed to load services. Please try again later.");
        setLoading(false);
      });
  }, []);

  return (
    <div className="min-h-screen bg-gray-50 p-6 lg:p-8 font-sans">
      <h1 className="text-5xl font-extrabold text-center text-gray-900 mb-12 tracking-tight">
        System Dashboard
      </h1>

      {/* Loading and Error States */}
      {loading && (
        <div className="text-center text-gray-600 text-xl py-10">Loading dashboard data...</div>
      )}
      {error && (
        <div className="text-center text-red-600 text-xl py-6 bg-red-50 rounded-lg shadow-md mx-auto max-w-2xl">
          {error}
        </div>
      )}

      {/* If not loading and no error */}
      {!loading && !error && (
        <>
          {/* Uptime Graph Card */}
          <div className="bg-white p-8 rounded-xl shadow-xl mb-12 max-w-3xl mx-auto">
            {services.length > 0 ? (
              <UptimeGraph serviceData={services} />
            ) : (
              <div className="text-center text-gray-500 py-10 text-lg">
                No service data available. Please add services in Service Management.
              </div>
            )}
          </div>

          {/* Services Overview Section */}
          <h2 className="text-3xl font-bold text-gray-900 mb-6 text-center sm:text-left">
            Services Overview
          </h2>

          {/* Services List */}
          {services.length === 0 ? (
            <div className="bg-white p-8 rounded-xl shadow-xl text-center text-gray-500 py-8 text-lg max-w-3xl mx-auto">
              No services configured yet.
            </div>
          ) : (
            <ul className="bg-white p-6 rounded-xl shadow-xl divide-y divide-gray-200 max-w-3xl mx-auto">
              {services.map((service) => (
                <li key={service.id} className="flex items-center justify-between py-4">
                  <span className="text-lg font-medium text-gray-800">{service.name}</span>
                  <span
                    className={`px-4 py-2 rounded-full text-sm font-semibold capitalize transition-all duration-300 
                      ${service.status === 'Operational' ? 'bg-green-100 text-green-800' : ''}
                      ${service.status === 'Degraded' || service.status === 'Partial Outage' ? 'bg-yellow-100 text-yellow-800' : ''}
                      ${service.status === 'Major Outage' ? 'bg-red-100 text-red-800' : ''}`}
                  >
                    {service.status}
                  </span>
                </li>
              ))}
            </ul>
          )}
        </>
      )}

      {/* Font Import - Use Inter for modern typography */}
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700;800&display=swap');
        body { font-family: 'Inter', sans-serif; }
      `}</style>
    </div>
  );
};

export default Dashboard;
