import { Home, Activity, Settings, Users, Lock, Menu } from "lucide-react";
import { Link, useLocation } from "react-router-dom";
import { useState } from "react";

const Sidebar = () => {
  const location = useLocation();
  const [isOpen, setIsOpen] = useState(false);

  const navItems = [
    { to: "/dashboard", icon: Home, label: "Dashboard" },
    { to: "/incidents", icon: Activity, label: "Incidents" },
    { to: "/services", icon: Settings, label: "Services" },
    { to: "/teams", icon: Users, label: "Teams" },
    { to: "/status", icon: Lock, label: "Public Status Page" },
  ];

  return (
    <>
      {/* Mobile toggle button */}
      <button
        className="lg:hidden fixed top-4 left-4 z-20 bg-indigo-600 text-white p-2 rounded-md shadow-md"
        onClick={() => setIsOpen(!isOpen)}
      >
        <Menu className="w-6 h-6" />
      </button>

      {/* Sidebar */}
      <aside
        className={`fixed top-0 left-0 h-full z-10 transform ${
          isOpen ? "translate-x-0" : "-translate-x-full"
        } lg:translate-x-0 transition-transform duration-300 ease-in-out w-64 bg-gradient-to-r from-blue-200 to-blue-300 shadow-xl rounded-r-xl`}
      >
        <div className="p-6 text-2xl font-bold text-indigo-700 flex items-center">
          <span className="text-3xl mr-2">ðŸ”§</span>
          StatusBoard
        </div>

        {/* Profile */}
        <div className="px-6 py-4 mt-4 mb-6 flex items-center space-x-4 cursor-pointer hover:bg-indigo-100 transition duration-300 rounded-lg">
          <div className="w-12 h-12 bg-gray-300 rounded-full flex items-center justify-center text-xl font-semibold text-gray-800">
            AB
          </div>
          <div className="text-gray-800 font-semibold">Hello, AB</div>
        </div>

        {/* Navigation */}
        <nav className="mt-6 space-y-2">
          {navItems.map(({ to, icon: Icon, label }) => (
            <Link
              key={to}
              to={to}
              onClick={() => setIsOpen(false)} // Close on mobile nav click
              className={`flex items-center px-6 py-3 hover:bg-gradient-to-r hover:from-indigo-300 hover:to-indigo-400 rounded-lg text-gray-800 transition duration-300 ${
                location.pathname === to ? "bg-indigo-200 text-white" : ""
              }`}
            >
              <Icon className="w-6 h-6 mr-3 text-indigo-600" />
              {label}
            </Link>
          ))}
        </nav>
      </aside>

      {/* Overlay for mobile when sidebar is open */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-30 z-0 lg:hidden"
          onClick={() => setIsOpen(false)}
        />
      )}
    </>
  );
};

export default Sidebar;
