import React, { useState, useEffect } from "react";
import axios from "axios";

const MemberList = ({ orgId }) => {
  const [members, setMembers] = useState([]);
  const [email, setEmail] = useState("");
  const [role, setRole] = useState("member");

  const fetchMembers = async () => {
    const res = await axios.get(`http://localhost:8001/organizations/${orgId}/members`);
    setMembers(res.data);
  };

  useEffect(() => {
    if (orgId) fetchMembers();
  }, [orgId]);

  const handleAdd = async (e) => {
    e.preventDefault();
    await axios.post("http://localhost:8001/organizations/members/", {
      id: crypto.randomUUID(),
      user_email: email,
      role,
      organization_id: orgId,
    });
    setEmail("");
    fetchMembers();
  };

  return (
    <div className="p-4 border mt-4">
      <h3 className="font-bold">Add Member to Org</h3>
      <form onSubmit={handleAdd} className="mb-3">
        <input
          type="email"
          placeholder="Email"
          className="border px-2 py-1 mr-2"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <select value={role} onChange={(e) => setRole(e.target.value)} className="mr-2 px-2 py-1">
          <option value="member">Member</option>
          <option value="admin">Admin</option>
        </select>
        <button className="bg-green-500 text-white px-3 py-1 rounded">Add</button>
      </form>

      <ul className="list-disc ml-5">
        {members.map((m) => (
          <li key={m.id}>{m.user_email} - {m.role}</li>
        ))}
      </ul>
    </div>
  );
};

export default MemberList;
