import React, { useState, useEffect } from "react";
import axios from "axios";
import Label from "../components/ui/label"; // Assuming Label.jsx

import Input from "../components/ui/input";   // Assuming Input.jsx

import Button from "../components/ui/button";

import Select from 'react-select';

import emailjs from "emailjs-com";


const IncidentManagement = () => {
  const [incidents, setIncidents] = useState([]);
  const [formIncident, setFormIncident] = useState({
    title: "",
    description: "",
    status: "investigating",
    services: [],
    type: "incident",
    email: "",
  });
  const [editingIncidentId, setEditingIncidentId] = useState(null);
  const [availableServices, setAvailableServices] = useState([]);
  const [filter, setFilter] = useState("");

  // Fetch incidents from backend
  const fetchIncidents = async () => {
    try {
      const res = await axios.get(`http://localhost:8001/incidents/${filter ? `?days=${filter}` : ""}`);
      setIncidents(res.data);
    } catch (error) {
      console.error("Error fetching incidents:", error);
    }
  };

  // Fetch available services for the multi-select dropdown
  const fetchAvailableServices = async () => {
    try {
      const response = await axios.get("http://localhost:8001/services/");
      setAvailableServices(response.data);
    } catch (error) {
      console.error("Error fetching available services:", error);
    }
  };

  // Initial data fetch on component mount and when filter changes
  useEffect(() => {
    fetchIncidents();
    fetchAvailableServices();
  }, [filter]);

  // Handle changes in form input fields (except react-select)
  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormIncident((prev) => ({ ...prev, [name]: value }));
  };

  // Handle form submission for creating or updating an incident
  const handleSubmit = async () => {
    try {
      if (editingIncidentId) {
        // Update existing incident
        const response = await axios.put(
          `http://localhost:8001/incidents/${editingIncidentId}`,
          formIncident
        );
        setIncidents((prev) =>
          prev.map((incident) =>
            incident.id === editingIncidentId ? response.data : incident
          )
        );
        setEditingIncidentId(null); // Exit editing mode
      } else {
        // Create new incident
        const response = await axios.post("http://localhost:8001/incidents/", formIncident);
        setIncidents((prev) => [...prev, response.data]);
      }

      // Reset form fields after successful submission
      setFormIncident({
        title: "",
        description: "",
        status: "investigating",
        services: [],
        type: "incident",
        email: "",
      });
    } catch (error) {
      console.error("Error submitting incident:", error.response ? error.response.data : error.message);
      alert(`Error submitting incident: ${error.response?.data?.detail || error.message}`);
    }
  };

  // Function to send email alerts using EmailJS
  const sendEmail = (to_email, subject, message) => {
    emailjs.send(
      "service_a3ubtln", // Your EmailJS Service ID
      "template_ef5rfrb", // Your EmailJS Template ID
      {
        to_email,
        subject,
        message,
      },
      "X2AdJAfMlruyyGzgb" // Your EmailJS Public Key
    )
    .then((response) => {
      console.log("✅ Email sent successfully", response.status, response.text);
      alert("Email sent successfully!");
    })
    .catch((error) => {
      console.error("❌ Error sending email", error);
      alert(`Failed to send email: ${error.text || error.message}`);
    });
  };

  // Populate form with incident data when "Edit" is clicked
  const startEditing = (incident) => {
    setEditingIncidentId(incident.id);
    // Map service names back to IDs for react-select to display correctly
    const serviceIds = incident.services.map(serviceName => {
      const service = availableServices.find(s => s.name === serviceName);
      return service ? service.id : serviceName;
    });
    setFormIncident({
      title: incident.title,
      description: incident.description,
      status: incident.status,
      services: serviceIds || [],
      type: incident.type,
      email: incident.email || "",
    });
  };

  // Clear form and exit editing mode
  const cancelEditing = () => {
    setEditingIncidentId(null);
    setFormIncident({
      title: "",
      description: "",
      status: "investigating",
      services: [],
      type: "incident",
      email: "",
    });
  };

  // Delete an incident
  const deleteIncident = async (id) => {
    try {
      await axios.delete(`http://localhost:8001/incidents/${id}`);
      setIncidents((prev) => prev.filter((incident) => incident.id !== id));
    } catch (error) {
      console.error("Error deleting incident:", error);
      alert(`Error deleting incident: ${error.message}`);
    }
  };

  // Format available services for react-select
  const serviceOptions = availableServices.map(service => ({
    value: service.id,
    label: service.name
  }));

  return (
    <div className="container mx-auto p-6 bg-gray-50 min-h-screen font-inter"> {/* Added font-inter */}
      <h1 className="text-4xl font-extrabold mb-8 text-center text-gray-800">Incident Management Dashboard</h1>

      {/* Filter Dropdown */}
      <div className="mb-8 flex justify-end">
        <select
          onChange={(e) => setFilter(e.target.value)}
          className="p-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 transition duration-200"
          value={filter}
        >
          <option value="">All Incidents</option>
          <option value="7">Last 7 days</option>
          <option value="30">Last 30 days</option>
        </select>
        <style>{`
          @import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700;800&display=swap');
          body { font-family: 'Inter', sans-serif; }
        `}</style>
      </div>

      {/* Incident Form */}
      <div className="bg-white p-8 rounded-xl shadow-lg mb-10 border border-gray-200">
        <h2 className="text-3xl font-semibold mb-6 text-gray-700 border-b pb-4">
          {editingIncidentId ? "Edit Existing Incident" : "Create New Incident"}
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
          {/* Title */}
          <div className="flex flex-col"> {/* Added flex-col for proper stacking */}
            <Label htmlFor="title">Title</Label>
            <Input
              name="title"
              value={formIncident.title}
              onChange={handleFormChange}
              placeholder="e.g., Database Outage"
              // Removed className here, assuming Input component has default styling
            />
          </div>

          {/* Description */}
          <div className="flex flex-col"> {/* Added flex-col */}
            <Label htmlFor="description">Description</Label>
            <Input
              name="description"
              value={formIncident.description}
              onChange={handleFormChange}
              placeholder="Detailed explanation of the issue"
              // Removed className here
            />
          </div>

          {/* Email */}
          <div className="flex flex-col"> {/* Added flex-col */}
            <Label htmlFor="email">Notify Email</Label>
            <Input
              type="email"
              name="email"
              placeholder="recipient@example.com"
              value={formIncident.email}
              onChange={handleFormChange}
              // Removed className here
            />
          </div>

          {/* Status */}
          <div className="flex flex-col"> {/* Added flex-col */}
            <Label htmlFor="status">Status</Label>
            <select
              name="status"
              value={formIncident.status}
              onChange={handleFormChange}
              className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400 appearance-none bg-white pr-8"
            >
              <option value="investigating">Investigating</option>
              <option value="identified">Identified</option>
              <option value="monitoring">Monitoring</option>
              <option value="resolved">Resolved</option>
            </select>
          </div>

          {/* Type */}
          <div className="flex flex-col"> {/* Added flex-col */}
            <Label htmlFor="type">Type</Label>
            <select
              name="type"
              value={formIncident.type}
              onChange={handleFormChange}
              className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400 appearance-none bg-white pr-8"
            >
              <option value="incident">Incident</option>
              <option value="maintenance">Maintenance</option>
            </select>
          </div>

          {/* Services */}
          <div className="flex flex-col"> {/* Added flex-col */}
            <Label htmlFor="services">Affected Services</Label>
            <Select
              isMulti
              name="services"
              options={serviceOptions}
              value={formIncident.services.map(id => {
                const service = availableServices.find(s => s.id === id);
                return { value: service?.id, label: service?.name };
              })}
              onChange={(selectedOptions) => {
                const selectedIds = selectedOptions.map(opt => opt.value);
                setFormIncident(prev => ({ ...prev, services: selectedIds }));
              }}
              className="basic-multi-select"
              classNamePrefix="select"
              styles={{
                control: (provided) => ({
                  ...provided,
                  padding: '0.25rem', // Adjust padding to match input height
                  borderColor: '#D1D5DB', // Tailwind gray-300
                  borderRadius: '0.375rem', // Tailwind rounded-md
                  '&:hover': { borderColor: '#9CA3AF' }, // Tailwind gray-400
                  boxShadow: 'none', // Remove default box shadow
                  minHeight: '44px', // Match input height (p-3 adds 12px padding top+bottom, plus border)
                }),
                menu: (provided) => ({ ...provided, zIndex: 9999 })
              }}
            />
          </div>
        </div>

        {/* Buttons */}
        <div className="flex justify-start gap-4 mt-6">
          <Button
            onClick={handleSubmit}
            className="px-6 py-3 bg-blue-600 text-white font-semibold rounded-lg shadow-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition duration-300 ease-in-out"
          >
            {editingIncidentId ? "Update Incident" : "Create Incident"}
          </Button>
          {editingIncidentId && (
            <Button
              onClick={cancelEditing}
              className="px-6 py-3 bg-gray-500 text-white font-semibold rounded-lg shadow-md hover:bg-gray-600 focus:outline-none focus:ring-2 focus:ring-gray-400 focus:ring-offset-2 transition duration-300 ease-in-out"
            >
              Cancel
            </Button>
          )}
        </div>
      </div>

      {/* Incident List */}
      <div className="bg-white p-8 rounded-xl shadow-lg border border-gray-200">
        <h2 className="text-3xl font-semibold mb-6 text-gray-700 border-b pb-4">Existing Incidents</h2>
        {incidents.length === 0 ? (
          <p className="text-center text-gray-500 py-8 text-lg">No incidents found. Create one above!</p>
        ) : (
          <div className="overflow-x-auto shadow-md rounded-lg">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-100">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-bold text-gray-600 uppercase tracking-wider">Title</th>
                  <th className="px-6 py-3 text-left text-xs font-bold text-gray-600 uppercase tracking-wider">Description</th>
                  <th className="px-6 py-3 text-left text-xs font-bold text-gray-600 uppercase tracking-wider">Status</th>
                  <th className="px-6 py-3 text-left text-xs font-bold text-gray-600 uppercase tracking-wider">Type</th>
                  <th className="px-6 py-3 text-left text-xs font-bold text-gray-600 uppercase tracking-wider">Services</th>
                  <th className="px-6 py-3 text-left text-xs font-bold text-gray-600 uppercase tracking-wider">Email</th>
                  <th className="px-6 py-3 text-left text-xs font-bold text-gray-600 uppercase tracking-wider">Actions</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {incidents.map((incident) => (
                  <tr key={incident.id} className="hover:bg-gray-50 transition-colors duration-150">
                    <td className="px-6 py-4 text-sm font-medium text-gray-900">{incident.title}</td>
                    <td className="px-6 py-4 text-sm text-gray-700 max-w-xs overflow-hidden text-ellipsis whitespace-nowrap" title={incident.description}>
                      {incident.description}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700 capitalize">{incident.status}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700 capitalize">{incident.type}</td>
                    <td className="px-6 py-4 text-sm text-gray-700 max-w-xs overflow-hidden text-ellipsis whitespace-nowrap" title={incident.services.join(", ")}>
                      {incident.services.join(", ")}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-blue-600 truncate" title={incident.email}>
                      {incident.email}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                      <div className="flex gap-2">
                        <Button
                          onClick={() => startEditing(incident)}
                          className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-400"
                        >
                          Edit
                        </Button>
                        <Button
                          onClick={() => deleteIncident(incident.id)}
                          className="px-4 py-2 bg-red-500 text-white rounded-md hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-red-400"
                        >
                          Delete
                        </Button>
                        {incident.email && (
                          <Button
                            onClick={() => sendEmail(incident.email, `Incident Alert: ${incident.title}`, `${incident.description}\nStatus: ${incident.status}`)}
                            className="px-4 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 focus:outline-none focus:ring-2 focus:ring-green-400"
                          >
                            Send Alert
                          </Button>
                        )}
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default IncidentManagement;
