import { Home, Activity, Settings } from "lucide-react";
import { Link, useLocation } from "react-router-dom";

const Sidebar = () => {
  const location = useLocation(); // Get the current route to highlight active links

  return (
    <aside className="h-screen w-64 bg-white shadow-md fixed left-0 top-0 z-10">
      <div className="p-6 text-2xl font-bold text-indigo-600">
        ðŸ”§ StatusBoard
      </div>
      <nav className="mt-6 space-y-2">
        <Link
          to="/dashboard"
          className={`flex items-center px-6 py-3 hover:bg-indigo-50 text-gray-800 ${
            location.pathname === "/dashboard" ? "bg-indigo-100" : ""
          }`}
        >
          <Home className="w-5 h-5 mr-3" />
          Dashboard
        </Link>
        <Link
          to="/incidents"
          className={`flex items-center px-6 py-3 hover:bg-indigo-50 text-gray-800 ${
            location.pathname === "/incidents" ? "bg-indigo-100" : ""
          }`}
        >
          <Activity className="w-5 h-5 mr-3" />
          Incidents
        </Link>
        <Link
          to="/services"
          className={`flex items-center px-6 py-3 hover:bg-indigo-50 text-gray-800 ${
            location.pathname === "/services" ? "bg-indigo-100" : ""
          }`}
        >
          <Settings className="w-5 h-5 mr-3" />
          Services
        </Link>
        <Link
          to="/teams"
          className={`flex items-center px-6 py-3 hover:bg-indigo-50 text-gray-800 ${
            location.pathname === "/teams" ? "bg-indigo-100" : ""
          }`}
        >
          <Settings className="w-5 h-5 mr-3" />
          Teams
        </Link>
        <Link
          to="/status"
          className={`flex items-center px-6 py-3 hover:bg-indigo-50 text-gray-800 ${
            location.pathname === "/status" ? "bg-indigo-100" : ""
          }`}
        >
          <Settings className="w-5 h-5 mr-3" />
          Public Status Page
        </Link>
      </nav>
    </aside>
  );
};

export default Sidebar;
