import React, { useState, useEffect } from "react";
import axios from "axios";
import Label from "../components/ui/label"; // Assuming Label.jsx

import Input from "../components/ui/input";   // Assuming Input.jsx

import Button from "../components/ui/button";

import Select from 'react-select';


const IncidentManagement = () => {
  const [incidents, setIncidents] = useState([]);
  const [formIncident, setFormIncident] = useState({
    title: "",
    description: "",
    status: "investigating",
    services: [],
    type: "incident",
    email: "", // ✅ new email field
  });
  const [editingIncidentId, setEditingIncidentId] = useState(null);
  const [availableServices, setAvailableServices] = useState([]);
  const [filter, setFilter] = useState("");

  const fetchIncidents = async () => {
    try {
      const res = await axios.get(`http://localhost:8001/incidents${filter ? `?days=${filter}` : ""}`);
      setIncidents(res.data);
    } catch (error) {
      console.error("Error fetching incidents:", error);
    }
  };

  useEffect(() => {
    fetchIncidents();
    fetchAvailableServices();
  }, [filter]);

  const fetchAvailableServices = async () => {
    try {
      const response = await axios.get("http://localhost:8001/services/");
      setAvailableServices(response.data);
    } catch (error) {
      console.error("Error fetching available services:", error);
    }
  };

  const handleFormChange = (e) => {
    const { name, value, options } = e.target;

    if (name === "services") {
      const selectedServices = Array.from(options)
        .filter(option => option.selected)
        .map(option => option.value);
      setFormIncident((prev) => ({ ...prev, services: selectedServices }));
    } else {
      setFormIncident((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async () => {
    try {
      if (editingIncidentId) {
        const response = await axios.put(
          `http://localhost:8001/incidents/${editingIncidentId}`,
          formIncident
        );
        setIncidents((prev) =>
          prev.map((incident) =>
            incident.id === editingIncidentId ? response.data : incident
          )
        );
        setEditingIncidentId(null);
      } else {
        const response = await axios.post("http://localhost:8001/incidents/", formIncident);
        setIncidents((prev) => [...prev, response.data]);

        // ✅ Send email alert
        if (formIncident.email) {
          sendEmail(
            formIncident.email,
            `Incident Alert: ${formIncident.title}`,
            `${formIncident.description}\nStatus: ${formIncident.status}`
          );
        }
      }

      setFormIncident({
        title: "",
        description: "",
        status: "investigating",
        services: [],
        type: "incident",
        email: "",
      });
    } catch (error) {
      console.error("Error submitting incident:", error.response ? error.response.data : error.message);
      if (error.response?.data?.detail) {
        alert(`Error: ${JSON.stringify(error.response.data.detail)}`);
      }
    }
  };

  const startEditing = (incident) => {
    setEditingIncidentId(incident.id);
    setFormIncident({
      title: incident.title,
      description: incident.description,
      status: incident.status,
      services: incident.services || [],
      type: incident.type,
      email: incident.email || "",
    });
  };

  const cancelEditing = () => {
    setEditingIncidentId(null);
    setFormIncident({
      title: "",
      description: "",
      status: "investigating",
      services: [],
      type: "incident",
      email: "",
    });
  };

  const deleteIncident = async (id) => {
    try {
      await axios.delete(`http://localhost:8001/incidents/${id}`);
      setIncidents((prev) => prev.filter((incident) => incident.id !== id));
    } catch (error) {
      console.error("Error deleting incident:", error);
    }
  };


  const serviceOptions = availableServices.map(service => ({
  value: service.id,
  label: service.name
}));
  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6 text-center text-gray-800">Incident Management</h1>

      {/* Filter Dropdown */}
      <div className="mb-4">
        <select onChange={(e) => setFilter(e.target.value)} className="p-2 border rounded">
          <option value="">All Incidents</option>
          <option value="7">Last 7 days</option>
          <option value="30">Last 30 days</option>
        </select>
      </div>

      {/* Incident Form */}
      <div className="bg-white p-6 rounded-lg shadow-xl mb-8 border border-gray-200">
        <h2 className="text-2xl font-semibold mb-4 text-gray-700">
          {editingIncidentId ? "Edit Incident" : "Create New Incident"}
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
          {/* Title */}
          <div>
            <Label htmlFor="title">Title</Label>
            <Input name="title" value={formIncident.title} onChange={handleFormChange} />
          </div>

          {/* Description */}
          <div>
            <Label htmlFor="description">Description</Label>
            <Input name="description" value={formIncident.description} onChange={handleFormChange} />
          </div>

          {/* Email */}
          <div>
            <Label htmlFor="email">Notify Email</Label>
            <Input
              type="email"
              name="email"
              placeholder="recipient@example.com"
              value={formIncident.email}
              onChange={handleFormChange}
            />
          </div>

          {/* Status */}
          <div>
            <Label htmlFor="status">Status</Label>
            <select name="status" value={formIncident.status} onChange={handleFormChange} className="w-full p-2 border rounded">
              <option value="investigating">Investigating</option>
              <option value="identified">Identified</option>
              <option value="monitoring">Monitoring</option>
              <option value="resolved">Resolved</option>
            </select>
          </div>

          {/* Type */}
          <div>
            <Label htmlFor="type">Type</Label>
            <select name="type" value={formIncident.type} onChange={handleFormChange} className="w-full p-2 border rounded">
              <option value="incident">Incident</option>
              <option value="maintenance">Maintenance</option>
            </select>
          </div>

          {/* Services */}
          <div>
            <Label htmlFor="services">Affected Services</Label>
           <Select
  isMulti
  name="services"
  options={serviceOptions}
  value={formIncident.services.map(id => {
    const service = availableServices.find(s => s.id === id);
    return { value: service?.id, label: service?.name };
  })}
  onChange={(selectedOptions) => {
    const selectedIds = selectedOptions.map(opt => opt.value);
    setFormIncident(prev => ({ ...prev, services: selectedIds }));
  }}
  className="basic-multi-select"
  classNamePrefix="select"
/>
          </div>
        </div>

        {/* Buttons */}
        <div className="flex gap-4">
          <Button onClick={handleSubmit}>
            {editingIncidentId ? "Update Incident" : "Create Incident"}
          </Button>
          {editingIncidentId && (
            <Button onClick={cancelEditing} className="bg-gray-500 hover:bg-gray-600 text-white">
              Cancel
            </Button>
          )}
        </div>
      </div>

      {/* Incident List */}
      <div className="bg-white p-6 rounded-lg shadow-xl border border-gray-200">
        <h2 className="text-2xl font-semibold mb-4 text-gray-700">Existing Incidents</h2>
        {incidents.length === 0 ? (
          <p className="text-center text-gray-600">No incidents created yet.</p>
        ) : (
          <table className="w-full border">
            <thead className="bg-gray-100">
              <tr>
                <th className="p-2 text-left">Title</th>
                <th className="p-2 text-left">Description</th>
                <th className="p-2 text-left">Type</th>
                <th className="p-2 text-left">Status</th>
                <th className="p-2 text-left">Email</th>
                <th className="p-2 text-left">Services</th>
                <th className="p-2 text-left">Actions</th>
              </tr>
            </thead>
            <tbody>
              {incidents.map(incident => (
                <tr key={incident.id} className="border-t">
                  <td className="p-2">{incident.title}</td>
                  <td className="p-2">{incident.description}</td>
                  <td className="p-2">{incident.type}</td>
                  <td className="p-2">{incident.status}</td>
                  <td className="p-2">{incident.email || "N/A"}</td>
                  <td className="p-2">
                    {incident.services.map(serviceId => {
                      const service = availableServices.find(s => s.id === serviceId);
                      return service?.name || serviceId;
                    }).join(", ")}
                  </td>
                  <td className="p-2 flex gap-2">
                    <Button onClick={() => startEditing(incident)} className="bg-green-600">Edit</Button>
                    <Button onClick={() => deleteIncident(incident.id)} className="bg-red-600">Delete</Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default IncidentManagement;
