import React, { useState, useEffect } from "react";
import axios from "axios";
import Label from "../components/ui/label";
import Input from "../components/ui/input";
import Button from "../components/ui/button";
import Sidebar from '../components/Sidebar';
import Navbar from '../components/Navbar';

const ServiceManagement = () => {
  const [services, setServices] = useState([]);
  const [formService, setFormService] = useState({ // Renamed from newService to formService
    name: "",
    description: "",
    status: "Operational",
  });
  const [editingServiceId, setEditingServiceId] = useState(null); // State to hold the ID of the service being edited

  useEffect(() => {
    fetchServices();
  }, []);

  const fetchServices = async () => {
    try {
      const response = await axios.get("http://localhost:8001/services");
      setServices(response.data);
    } catch (error) {
      console.error("Error fetching services:", error);
      // Optional: Add user-facing error message
    }
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormService((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async () => {
    try {
      if (editingServiceId) {
        // If editingServiceId is set, it's an update operation
        const response = await axios.put(
          `http://localhost:8001/services/${editingServiceId}`,
          formService // Send the data from the form
        );
        setServices((prev) =>
          prev.map((service) =>
            service.id === editingServiceId ? response.data : service
          )
        );
        setEditingServiceId(null); // Clear editing state
      } else {
        // Otherwise, it's a create operation
        const response = await axios.post(
          "http://localhost:8001/services",
          formService // Send the data from the form
        );
        setServices((prev) => [...prev, response.data]);
      }
      // Reset form after create/update
      setFormService({ name: "", description: "", status: "Operational" });
    } catch (error) {
      console.error("Error submitting service:", error.response ? error.response.data : error.message);
      // Optional: Add user-facing error message
    }
  };

  const startEditing = (service) => {
    setEditingServiceId(service.id);
    setFormService({
      name: service.name,
      description: service.description,
      status: service.status,
    });
  };

  const cancelEditing = () => {
    setEditingServiceId(null);
    setFormService({ name: "", description: "", status: "Operational" });
  };

  const deleteService = async (id) => {
    try {
      await axios.delete(`http://localhost:8001/services/${id}`);
      setServices((prev) => prev.filter((service) => service.id !== id));
    } catch (error) {
      console.error("Error deleting service:", error);
      // Optional: Add user-facing error message
    }
  };

  return (
   <div className="min-h-screen w-full overflow-x-hidden bg-gray-50 px-4 py-6 relative">
      {/* Sidebar toggle (fake for now) */}
      <div className="absolute top-4 left-4 z-50">
        <button className="p-2 rounded-md bg-white shadow-md">
          <Menu />
        </button>
      </div>

      <div className="max-w-3xl mx-auto mt-12">
        <h1 className="text-2xl font-bold mb-6 text-center">Service Management</h1>

        {/* Form */}
        <div className="bg-white rounded-xl shadow-md p-6 space-y-4">
          <h2 className="text-lg font-semibold">Create New Service</h2>

          <div className="space-y-2">
            <label className="block text-sm font-medium">Name</label>
            <input
              type="text"
              name="name"
              value={form.name}
              onChange={handleChange}
              placeholder="Service Name"
              className="w-full border rounded-md px-3 py-2"
            />
          </div>

          <div className="space-y-2">
            <label className="block text-sm font-medium">Description</label>
            <input
              type="text"
              name="description"
              value={form.description}
              onChange={handleChange}
              placeholder="Service Description"
              className="w-full border rounded-md px-3 py-2"
            />
          </div>

          <div className="space-y-2">
            <label className="block text-sm font-medium">Status</label>
            <select
              name="status"
              value={form.status}
              onChange={handleChange}
              className="w-full border rounded-md px-3 py-2"
            >
              <option>Operational</option>
              <option>Down</option>
              <option>Degraded</option>
            </select>
          </div>

          <button
            onClick={handleSubmit}
            className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            Create Service
          </button>
        </div>

        {/* Table */}
        <div className="mt-8">
          <h2 className="text-lg font-semibold mb-4">Existing Services</h2>
          <div className="overflow-x-auto">
            <table className="min-w-[500px] w-full text-sm border border-gray-200 rounded-md">
              <thead className="bg-gray-100 text-left">
                <tr>
                  <th className="px-4 py-2 border-b">Name</th>
                  <th className="px-4 py-2 border-b">Description</th>
                  <th className="px-4 py-2 border-b">Status</th>
                </tr>
              </thead>
              <tbody>
                {services.map((s, i) => (
                  <tr key={i} className="border-b">
                    <td className="px-4 py-2">{s.name}</td>
                    <td className="px-4 py-2">{s.description}</td>
                    <td className="px-4 py-2">
                      <span
                        className={`px-2 py-1 rounded-full text-xs font-medium ${
                          s.status === "Operational"
                            ? "bg-green-100 text-green-800"
                            : s.status === "Degraded"
                            ? "bg-yellow-100 text-yellow-800"
                            : "bg-red-100 text-red-800"
                        }`}
                      >
                        {s.status}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};
export default ServiceManagement;
