import React, { useEffect, useState } from "react";
import axios from "axios";

const statusColor = {
  operational: "green",
  degraded: "orange",
  down: "red",
};

const ServiceStatusList = () => {
  const [services, setServices] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8001/services/")
      .then((res) => setServices(res.data));
  }, []);

  return (
    <div>
      <h2 className="text-lg font-semibold mb-2">Services</h2>
      <ul className="space-y-2">
        {services.map((service) => (
          <li key={service.id} className="flex items-center justify-between border p-2 rounded">
            <span>{service.name}</span>
            <span className={`text-${statusColor[service.status]}-600 font-bold capitalize`}>
              {service.status}
            </span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ServiceStatusList;
