// src/pages/Incidents.jsx

import { useEffect, useState } from "react";
import { fetchIncidents, createIncident } from "../lib/api";
import IncidentForm from "../components/IncidentForm";
import { Button } from "../components/ui/button";
import { Card } from "../components/ui/Card";

export default function IncidentsPage() {
  const [incidents, setIncidents] = useState([]);
  const [showModal, setShowModal] = useState(false);

  const loadData = async () => {
    const data = await fetchIncidents();
    setIncidents(data);
  };

  useEffect(() => {
    loadData();
  }, []);

  const handleCreate = async (newIncident) => {
    await createIncident(newIncident);
    loadData();
  };

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-xl font-semibold">Incidents</h2>
        <Button onClick={() => setShowModal(true)}>+ Add Incident</Button>
      </div>
      <IncidentForm open={showModal} setOpen={setShowModal} onSubmit={handleCreate} />

      <div className="space-y-4">
        {incidents.map((incident) => (
          <Card key={incident.id} className="p-4">
            <h3 className="text-lg font-bold">{incident.title}</h3>
            <p className="text-sm text-muted-foreground">{incident.description}</p>
            <p className="text-xs mt-2">Status: <strong>{incident.status}</strong></p>
          </Card>
        ))}
      </div>
    </div>
  );
}
