import { Home, Activity, Settings, Users } from "lucide-react";
import { Link, useLocation } from "react-router-dom";

const Sidebar = () => {
  const location = useLocation(); // Get the current route to highlight active links

  return (
    <aside className="h-screen w-64 bg-gradient-to-r from-blue-100 to-blue-200 shadow-lg fixed left-0 top-0 z-10 rounded-r-xl">
      <div className="p-6 text-2xl font-bold text-indigo-700">
        ðŸ”§ StatusBoard
      </div>
      {/* Profile Section */}
      <div className="px-6 py-4 mt-4 mb-6 flex items-center space-x-4">
        <div className="w-10 h-10 bg-gray-200 rounded-full flex items-center justify-center text-lg font-bold text-gray-700">
          AB
        </div>
        <span className="text-gray-800 font-semibold">Hello, AB</span>
      </div>
      <nav className="mt-6 space-y-2">
        <Link
          to="/dashboard"
          className={`flex items-center px-6 py-3 hover:bg-indigo-50 rounded-lg text-gray-800 transition duration-300 ${
            location.pathname === "/dashboard" ? "bg-indigo-200" : ""
          }`}
        >
          <Home className="w-6 h-6 mr-3 text-indigo-600" />
          Dashboard
        </Link>
        <Link
          to="/incidents"
          className={`flex items-center px-6 py-3 hover:bg-indigo-50 rounded-lg text-gray-800 transition duration-300 ${
            location.pathname === "/incidents" ? "bg-indigo-200" : ""
          }`}
        >
          <Activity className="w-6 h-6 mr-3 text-indigo-600" />
          Incidents
        </Link>
        <Link
          to="/services"
          className={`flex items-center px-6 py-3 hover:bg-indigo-50 rounded-lg text-gray-800 transition duration-300 ${
            location.pathname === "/services" ? "bg-indigo-200" : ""
          }`}
        >
          <Settings className="w-6 h-6 mr-3 text-indigo-600" />
          Services
        </Link>
        <Link
          to="/teams"
          className={`flex items-center px-6 py-3 hover:bg-indigo-50 rounded-lg text-gray-800 transition duration-300 ${
            location.pathname === "/teams" ? "bg-indigo-200" : ""
          }`}
        >
          <Users className="w-6 h-6 mr-3 text-indigo-600" />
          Teams
        </Link>
        <Link
          to="/status"
          className={`flex items-center px-6 py-3 hover:bg-indigo-50 rounded-lg text-gray-800 transition duration-300 ${
            location.pathname === "/status" ? "bg-indigo-200" : ""
          }`}
        >
          <Settings className="w-6 h-6 mr-3 text-indigo-600" />
          Public Status Page
        </Link>
      </nav>
    </aside>
  );
};

export default Sidebar;
