import React, { useContext, useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import AuthContext from '../contexts/AuthContext';
import axios from 'axios';

const AdminDashboard = () => {
  const { user, logout } = useContext(AuthContext);
  const [users, setUsers] = useState([]);
  const [feeds, setFeeds] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    totalUsers: 0,
    activeUsers: 0,
    totalFeeds: 0,
    reportedFeeds: 0
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [usersRes, feedsRes, statsRes] = await Promise.all([
          axios.get('/api/admin/users', {
            headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
          }),
          axios.get('/api/admin/feeds', {
            headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
          }),
          axios.get('/api/admin/stats', {
            headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
          })
        ]);
        
        setUsers(usersRes.data);
        setFeeds(feedsRes.data);
        setStats(statsRes.data);
      } catch (error) {
        console.error('Error fetching admin data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleDeleteFeed = async (feedId) => {
    try {
      await axios.delete(`/api/admin/feeds/${feedId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setFeeds(feeds.filter(feed => feed._id !== feedId));
    } catch (error) {
      console.error('Error deleting feed:', error);
    }
  };

  if (loading) return <div>Loading admin dashboard...</div>;

  return (
    <div className="container mx-auto p-4">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-2xl font-bold">Admin Dashboard</h1>
        <div className="flex space-x-4">
          <button onClick={logout} className="text-red-600">Logout</button>
        </div>
      </div>

      {/* Stats Overview */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-8">
        <StatCard title="Total Users" value={stats.totalUsers} />
        <StatCard title="Active Users" value={stats.activeUsers} />
        <StatCard title="Total Feeds" value={stats.totalFeeds} />
        <StatCard title="Reported Feeds" value={stats.reportedFeeds} />
      </div>

      {/* Feeds Management */}
      <div className="mb-8">
        <h2 className="text-xl font-semibold mb-4">Feed Management</h2>
        <div className="space-y-4">
          {feeds.map(feed => (
            <FeedCard 
              key={feed._id} 
              feed={feed} 
              onDelete={handleDeleteFeed} 
            />
          ))}
        </div>
      </div>

      {/* Users Management */}
      <div>
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold">User Management</h2>
          <Link to="/admin/users/create" className="btn-primary">
            Add User
          </Link>
        </div>
        <UserTable users={users} />
      </div>
    </div>
  );
};

// Helper Components
const StatCard = ({ title, value }) => (
  <div className="bg-white p-4 rounded shadow">
    <h3 className="text-gray-500">{title}</h3>
    <p className="text-2xl font-bold">{value}</p>
  </div>
);

const FeedCard = ({ feed, onDelete }) => (
  <div className="bg-white p-4 rounded shadow">
    <h3 className="font-medium">{feed.title}</h3>
    <p className="text-sm text-gray-500 mb-2">
      Posted by: {feed.userEmail} • {new Date(feed.createdAt).toLocaleString()}
    </p>
    <div className="flex space-x-2">
      <button 
        onClick={() => onDelete(feed._id)}
        className="btn-danger"
      >
        Delete
      </button>
      {feed.reported && <span className="btn-warning">Reported</span>}
    </div>
  </div>
);

const UserTable = ({ users }) => (
  <div className="overflow-x-auto">
    <table className="min-w-full bg-white rounded shadow">
      <thead>
        <tr>
          <TableHeader>Email</TableHeader>
          <TableHeader>Role</TableHeader>
          <TableHeader>Joined</TableHeader>
          <TableHeader>Actions</TableHeader>
        </tr>
      </thead>
      <tbody>
        {users.map(user => (
          <tr key={user._id} className="border-t">
            <TableCell>{user.email}</TableCell>
            <TableCell>
              <span className={`badge-${user.role === 'admin' ? 'purple' : 'blue'}`}>
                {user.role}
              </span>
            </TableCell>
            <TableCell>
              {new Date(user.createdAt).toLocaleDateString()}
            </TableCell>
            <TableCell>
              <Link 
                to={`/admin/users/edit/${user._id}`}
                className="text-blue-600 mr-2"
              >
                Edit
              </Link>
              <button className="text-red-600">Delete</button>
            </TableCell>
          </tr>
        ))}
      </tbody>
    </table>
  </div>
);

const TableHeader = ({ children }) => (
  <th className="px-4 py-2 text-left">{children}</th>
);

const TableCell = ({ children }) => (
  <td className="px-4 py-2">{children}</td>
);

export default AdminDashboard;
