import React, { useState, useEffect } from "react";
import axios from "axios";

const MemberList = ({ orgId }) => {
  const [members, setMembers] = useState([]);
  const [email, setEmail] = useState("");
  const [role, setRole] = useState("member");

  const fetchMembers = async () => {
    const res = await axios.get(`http://localhost:8001/organizations/${orgId}/members`);
    setMembers(res.data);
  };

  useEffect(() => {
    if (orgId) fetchMembers();
  }, [orgId]);

  const handleAdd = async (e) => {
    e.preventDefault();
    await axios.post("http://localhost:8001/organizations/members/", {
      id: crypto.randomUUID(),
      user_email: email,
      role,
      organization_id: orgId,
    });
    setEmail("");
    fetchMembers();
  };

  return (
    <div className="p-6 bg-white shadow-md rounded-lg mt-6">
      <h3 className="text-2xl font-semibold text-gray-800 mb-4">Add Member to Organization</h3>
      <form onSubmit={handleAdd} className="space-y-4">
        <div className="flex space-x-4">
          <input
            type="email"
            placeholder="Enter Email"
            className="border border-gray-300 rounded-lg px-4 py-2 w-full focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <select
            value={role}
            onChange={(e) => setRole(e.target.value)}
            className="border border-gray-300 rounded-lg px-4 py-2 w-1/4 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <option value="member">Member</option>
            <option value="admin">Admin</option>
          </select>
          <button
            type="submit"
            className="bg-green-600 text-white px-6 py-2 rounded-lg font-semibold hover:bg-green-700 transition duration-200"
          >
            Add
          </button>
        </div>
      </form>

      <div className="mt-6">
        <h4 className="text-xl font-medium text-gray-700 mb-3">Current Members</h4>
        <ul className="space-y-2">
          {members.map((m) => (
            <li
              key={m.id}
              className="flex justify-between items-center p-3 border border-gray-200 rounded-lg"
            >
              <span className="text-gray-700">{m.user_email}</span>
              <span
                className={`text-sm px-3 py-1 rounded-full ${
                  m.role === "admin"
                    ? "bg-blue-100 text-blue-800"
                    : "bg-green-100 text-green-800"
                }`}
              >
                {m.role}
              </span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default MemberList;
