import React, { useState, useEffect } from "react";
import axios from "axios";
import OrganizationForm from "./OrganizationForm";
import MemberList from "./MemberList";
import Sidebar from '../components/Sidebar';
import Navbar from '../components/Navbar';

const Teams = () => {
  const [organizations, setOrganizations] = useState([]);
  const [selectedOrgId, setSelectedOrgId] = useState(null);

  useEffect(() => {
    const fetchOrgs = async () => {
      try {
        const res = await axios.get("http://localhost:8001/organizations/");
        setOrganizations(res.data);
      } catch (error) {
        console.error("Failed to fetch organizations:", error);
      }
    };
    fetchOrgs();
  }, []);

  return (
       <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white flex">
      {/* Sidebar - Visible on medium screens and up, or toggled open on small screens */}
      <div className={`fixed inset-y-0 left-0 transform ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'} md:relative md:translate-x-0 transition-transform duration-200 ease-in-out z-30 w-64`}>
        <Sidebar toggleSidebar={toggleSidebar} /> {/* Pass toggle function to Sidebar if it has a close button */}
      </div>

      {/* Main Content Area */}
      <div className="flex-1 overflow-auto"> {/* flex-1 allows it to take remaining width, overflow-auto for scrolling */}
        {/* Navbar */}
        <Navbar toggleSidebar={toggleSidebar} /> {/* Pass toggle function to Navbar for a menu icon */}

        {/* Main Content Padding */}
        <div className="p-4 sm:p-6 md:p-8 max-w-7xl mx-auto">
          <h1 className="text-3xl sm:text-4xl font-extrabold text-blue-600 mb-6 text-center">
            Teams & Organizations
          </h1>

          {/* Organization Form */}
          <div className="mb-8">
            <OrganizationForm />
          </div>

          {/* Select Organization Section */}
          <div className="mt-8">
            <h2 className="text-xl sm:text-2xl font-semibold text-blue-600 mb-4 text-center">
              Select Organization
            </h2>

            <select
              onChange={(e) => setSelectedOrgId(e.target.value)}
              className="block w-full max-w-sm mx-auto mt-2 p-3 border border-gray-300 rounded-lg shadow-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-base sm:text-lg"
            >
              <option value="">-- Select --</option>
              {organizations.map((org) => (
                <option key={org.id} value={org.id}>
                  {org.name}
                </option>
              ))}
            </select>

            {selectedOrgId && (
              <div className="mt-8 bg-white rounded-lg shadow-md p-4 sm:p-6">
                <MemberList orgId={selectedOrgId} />
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Teams;
