import React from "react";
import ServiceStatusList from "../components/ServiceStatusList";
import IncidentTimeline from "../components/IncidentTimeline";
import Sidebar from "../components/Sidebar";
import Navbar from "../components/Navbar";

const PublicStatusPage = () => {
  return (
    // Outer container: full height, light gradient, flex for sidebar/main content
    <div className="min-h-screen bg-gradient-to-b from-white to-gray-50 flex">
      {/* Sidebar - assumed to handle its own responsiveness (e.g., hidden on mobile, fixed on large screens) */}
      <Sidebar />

      {/* Main content area: takes remaining space, adjusts margin for sidebar */}
      {/* `lg:ml-64` pushes content right only on large screens, accounting for a fixed sidebar */}
      {/* `p-4 sm:p-6 lg:p-8` for responsive padding */}
      <div className="flex-1 lg:ml-64 transition-all duration-300 p-4 sm:p-6 lg:p-8">
        <Navbar /> {/* The Navbar should be styled to have space below it, as discussed */}

        <main className="max-w-4xl mx-auto py-4 sm:py-6 lg:py-10 px-0 sm:px-4 lg:px-6"> {/* Responsive padding for main content */}
          <div className="text-center mb-8 sm:mb-10 lg:mb-12">
            <h1 className="text-3xl sm:text-4xl lg:text-5xl font-extrabold text-gray-800 mb-2 sm:mb-4 lg:mb-6 tracking-tight leading-tight">
              ðŸ“¡ System Status
            </h1>
            {/* You can add a subtitle or short description here if needed */}
            {/* <p className="text-lg text-gray-600">Real-time updates on our services.</p> */}
          </div>

          {/* Service Status List Card */}
          <div className="bg-blue-50 rounded-xl shadow-md p-6 sm:p-8 lg:p-10 mb-8 border border-blue-100 transform hover:scale-[1.02] transition-transform duration-300 ease-in-out">
            <ServiceStatusList />
          </div>

          {/* Incident Timeline Card */}
          <div className="bg-yellow-50 rounded-xl shadow-md p-6 sm:p-8 lg:p-10 border border-yellow-100 transform hover:scale-[1.02] transition-transform duration-300 ease-in-out">
            <IncidentTimeline />
          </div>
        </main>
      </div>
    </div>
  );
};

export default PublicStatusPage;
