import React, { useState, useEffect } from "react";
import axios from "axios";
import Label from "../components/ui/Label"; // Correct import path
import Input from "../components/ui/Input"; // Correct import path
import Button from "../components/ui/Button"; // Correct import path

const ServiceManagement = () => {
  const [services, setServices] = useState([]);
  const [newService, setNewService] = useState({
    name: "",
    description: "",
    status: "Operational",
  });

  useEffect(() => {
    fetchServices();
  }, []);

  const fetchServices = async () => {
    const response = await axios.get("http://localhost:8000/services");
    setServices(response.data);
  };

  const createService = async () => {
    const response = await axios.post("http://localhost:8000/services", newService);
    setServices((prev) => [...prev, response.data]);
    setNewService({ name: "", description: "", status: "Operational" });
  };

  const updateService = async (id) => {
    const response = await axios.put(`http://localhost:8001/services/${id}`, newService);
    setServices((prev) =>
      prev.map((service) => (service.id === id ? response.data : service))
    );
  };

  const deleteService = async (id) => {
    await axios.delete(`http://localhost:8000/services/${id}`);
    setServices((prev) => prev.filter((service) => service.id !== id));
  };

  return (
    <div>
      <h1>Service Management</h1>
      
      {/* New Service Form */}
      <div>
        <Label>Name</Label>
        <Input
          type="text"
          value={newService.name}
          onChange={(e) => setNewService({ ...newService, name: e.target.value })}
        />
        <Label>Description</Label>
        <Input
          type="text"
          value={newService.description}
          onChange={(e) => setNewService({ ...newService, description: e.target.value })}
        />
        <Label>Status</Label>
        <select
          value={newService.status}
          onChange={(e) => setNewService({ ...newService, status: e.target.value })}
        >
          <option value="Operational">Operational</option>
          <option value="Degraded">Degraded</option>
          <option value="Partial Outage">Partial Outage</option>
          <option value="Major Outage">Major Outage</option>
        </select>
        <Button onClick={createService}>Create Service</Button>
      </div>

      {/* Service List */}
      <div>
        <h2>Existing Services</h2>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Description</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {services.map((service) => (
              <tr key={service.id}>
                <td>{service.name}</td>
                <td>{service.description}</td>
                <td>{service.status}</td>
                <td>
                  <Button onClick={() => updateService(service.id)}>Update</Button>
                  <Button onClick={() => deleteService(service.id)}>Delete</Button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ServiceManagement;
