import React from "react";
import { useNavigate } from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    // Clear the token from localStorage
    localStorage.removeItem("token"); // or sessionStorage.removeItem("token");
    
    // Redirect the user to the login page
    navigate("/"); // Assuming / is the login route
  };

  return (
    <header className="ml-64 h-16 bg-white shadow-md flex items-center justify-between px-6">
      <h1 className="text-xl font-semibold text-gray-800">Your App</h1>
      <div className="flex items-center space-x-4">
        {/* Profile picture or initials */}
        <div className="w-10 h-10 bg-gray-300 rounded-full flex items-center justify-center text-sm font-semibold text-gray-800">
          <span>AB</span>
        </div>
        {/* Logout Button */}
        <button 
          className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition duration-200"
          onClick={handleLogout}>
          Logout
        </button>
      </div>
    </header>
  );
};

export default Navbar;
