import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const handleLogout = () => {
    // Clear the token from localStorage
    localStorage.removeItem("token"); // or sessionStorage.removeItem("token");

    // Redirect the user to the login page
    navigate("/"); // Assuming / is the login route
  };

  return (
    <header className="ml-64 w-full h-16 bg-gradient-to-r from-pink-200 to-teal-200 shadow-md flex items-center justify-between px-6">
      <h1 className="text-xl font-semibold text-gray-800">Your App</h1>
      <div className="flex items-center space-x-4 relative">
        {/* Profile Picture/Initials with Dropdown */}
        <div
          className="w-10 h-10 bg-gray-300 rounded-full flex items-center justify-center text-sm font-semibold text-gray-800 cursor-pointer"
          onClick={() => setDropdownOpen(!dropdownOpen)}
        >
          <span>AB</span>
        </div>
        {dropdownOpen && (
          <div className="absolute right-0 mt-2 w-40 bg-white rounded-md shadow-lg border border-gray-200">
            <button
              className="w-full text-left text-gray-700 px-4 py-2 hover:bg-gray-100"
              onClick={handleLogout}
            >
              Logout
            </button>
          </div>
        )}
      </div>
    </header>
  );
};

export default Navbar;
