import React, { useEffect, useState } from "react";
import axios from "axios";

const statusColor = {
  resolved: "text-green-600",
  investigating: "text-yellow-600",
  identified: "text-orange-600",
  monitoring: "text-blue-600",
  ongoing: "text-red-600",
};

const IncidentTimeline = () => {
  const [incidents, setIncidents] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8001/incidents/")
      .then((res) => setIncidents(res.data));
  }, []);

  return (
    <div className="mt-8 max-w-4xl mx-auto bg-white shadow-md rounded-xl p-6">
      <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">
        Recent Incidents
      </h2>

      {incidents.length === 0 ? (
        <p className="text-center text-gray-500">No incidents reported.</p>
      ) : (
        incidents.slice().reverse().map((incident) => (
          <div
            key={incident.id}
            className="border-l-4 border-blue-500 bg-gray-50 rounded-lg p-5 mb-6 shadow-sm hover:shadow-md transition"
          >
            <h3 className="text-xl font-semibold text-gray-800 mb-1">
              {incident.title}
            </h3>
            <p className="text-sm text-gray-500 mb-2">
              {new Date(incident.created_at).toLocaleString()}
            </p>
            <p className="mb-1">
              <strong>Status:</strong>{" "}
              <span className={`font-medium ${statusColor[incident.status] || "text-gray-700"}`}>
                {incident.status}
              </span>
            </p>
            <p className="mb-1">
              <strong>Type:</strong> {incident.type}
            </p>
            <p className="mt-2 text-gray-700 whitespace-pre-line">
              {incident.description}
            </p>
          </div>
        ))
      )}
    </div>
  );
};

export default IncidentTimeline;
