import { useState, useEffect } from 'react';
import axios from 'axios';
import Sidebar from '../components/Sidebar';
import Navbar from '../components/Navbar';
import UptimeGraph from '../components/UptimeGraph';

const Dashboard = () => {
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:8001/services/')
      .then((res) => {
        setServices(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching services:", err);
        setError("Failed to load services. Please try again later.");
        setLoading(false);
      });
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 via-white to-gray-200 px-6 py-10 sm:px-10 lg:px-20 font-inter">
      <h1 className="text-5xl font-extrabold text-center text-gray-800 mb-14">
        <span className="inline-block mr-2">ðŸš¦</span> System Status Dashboard
      </h1>

      {loading && (
        <div className="text-center text-gray-600 text-lg animate-pulse">Loading dashboard data...</div>
      )}

      {error && (
        <div className="bg-red-100 text-red-800 text-center p-4 rounded-lg shadow-md max-w-2xl mx-auto">
          {error}
        </div>
      )}

      {!loading && !error && (
        <>
          {/* Uptime Graph Section */}
          <div className="bg-white p-8 rounded-3xl shadow-2xl mb-16 max-w-5xl mx-auto border border-gray-200 hover:shadow-2xl transition-shadow">
            {services.length > 0 ? (
              <UptimeGraph serviceData={services} />
            ) : (
              <div className="text-center text-gray-500 py-12 text-xl">
                No service data available. Add services in Service Management.
              </div>
            )}
          </div>

          {/* Services Overview */}
          <h2 className="text-4xl font-bold text-gray-800 mb-10 text-center">
            ðŸ§© Services Overview
          </h2>

          {services.length === 0 ? (
            <div className="bg-white p-8 rounded-2xl shadow-xl text-center text-gray-500 py-10 text-lg max-w-4xl mx-auto">
              No services configured yet.
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 max-w-5xl mx-auto">
              {services.map((service) => (
                <div
                  key={service.id}
                  className="bg-white p-6 rounded-2xl shadow-md border border-gray-100 hover:shadow-lg transition-all flex justify-between items-center"
                >
                  <div>
                    <h3 className="text-xl font-semibold text-gray-800">{service.name}</h3>
                    <p className="text-sm text-gray-500">Service ID: {service.id}</p>
                  </div>
                  <span
                    className={`px-4 py-1.5 rounded-full text-sm font-semibold capitalize transition
                      ${service.status === 'Operational' ? 'bg-green-100 text-green-700' : ''}
                      ${service.status === 'Degraded' || service.status === 'Partial Outage' ? 'bg-yellow-100 text-yellow-700' : ''}
                      ${service.status === 'Major Outage' ? 'bg-red-100 text-red-700' : ''}
                    `}
                  >
                    {service.status}
                  </span>
                </div>
              ))}
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default Dashboard;
