import { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';

const AdminDashboard = () => {
  const { user, logout } = useAuth();
  const [users, setUsers] = useState([]);
  const [reportedContent, setReportedContent] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [creditUpdate, setCreditUpdate] = useState({ userId: '', amount: '' });

  // Fetch all users and reported content
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [usersRes, contentRes] = await Promise.all([
          api.get('/api/admin/users'),
          api.get('/api/admin/reported-content')
        ]);
        setUsers(usersRes.data);
        setReportedContent(contentRes.data);
      } catch (err) {
        setError('Failed to fetch data');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Update user credits
  const handleCreditUpdate = async (e) => {
    e.preventDefault();
    try {
      await api.put(`/api/admin/users/${creditUpdate.userId}/credits`, {
        amount: Number(creditUpdate.amount)
      });
      // Refresh users list
      const res = await api.get('/api/admin/users');
      setUsers(res.data);
      setCreditUpdate({ userId: '', amount: '' });
    } catch (err) {
      setError('Failed to update credits');
    }
  };

  // Moderate reported content
  const handleContentModeration = async (contentId, action) => {
    try {
      await api.patch(`/api/admin/content/${contentId}`, { action });
      // Refresh reported content
      const res = await api.get('/api/admin/reported-content');
      setReportedContent(res.data);
    } catch (err) {
      setError('Failed to moderate content');
    }
  };

  if (loading) return <div className="text-center py-8">Loading...</div>;
  if (error) return <div className="text-red-500 text-center py-8">{error}</div>;

  return (
    <div className="container mx-auto p-4">
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Admin Dashboard</h1>
        <div className="flex items-center space-x-4">
          <span className="font-medium">Welcome, {user?.username}</span>
          <button 
            onClick={logout}
            className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
          >
            Logout
          </button>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <div className="bg-white p-6 rounded-lg shadow">
          <h3 className="text-lg font-semibold mb-2">Total Users</h3>
          <p className="text-3xl font-bold">{users.length}</p>
        </div>
        <div className="bg-white p-6 rounded-lg shadow">
          <h3 className="text-lg font-semibold mb-2">Reported Content</h3>
          <p className="text-3xl font-bold">{reportedContent.length}</p>
        </div>
        <div className="bg-white p-6 rounded-lg shadow">
          <h3 className="text-lg font-semibold mb-2">Total Credits</h3>
          <p className="text-3xl font-bold">
            {users.reduce((sum, user) => sum + user.credits, 0)}
          </p>
        </div>
      </div>

      {/* User Management Section */}
      <div className="bg-white rounded-lg shadow-md p-6 mb-8">
        <h2 className="text-2xl font-semibold mb-4">User Management</h2>
        
        {/* Credit Update Form */}
        <form onSubmit={handleCreditUpdate} className="mb-6 p-4 bg-gray-50 rounded">
          <h3 className="text-lg font-medium mb-3">Update User Credits</h3>
          <div className="flex flex-wrap items-end gap-4">
            <div className="flex-1 min-w-[200px]">
              <label className="block text-sm font-medium mb-1">Select User</label>
              <select
                value={creditUpdate.userId}
                onChange={(e) => setCreditUpdate({...creditUpdate, userId: e.target.value})}
                className="w-full p-2 border rounded"
                required
              >
                <option value="">Select user</option>
                {users.map(user => (
                  <option key={user._id} value={user._id}>
                    {user.username} ({user.credits} credits)
                  </option>
                ))}
              </select>
            </div>
            <div className="flex-1 min-w-[200px]">
              <label className="block text-sm font-medium mb-1">Amount</label>
              <input
                type="number"
                value={creditUpdate.amount}
                onChange={(e) => setCreditUpdate({...creditUpdate, amount: e.target.value})}
                className="w-full p-2 border rounded"
                placeholder="Enter amount"
                required
              />
            </div>
            <button
              type="submit"
              className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
            >
              Update Credits
            </button>
          </div>
        </form>

        {/* Users Table */}
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Username</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Email</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Credits</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Joined</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {users.map((user) => (
                <tr key={user._id}>
                  <td className="px-6 py-4 whitespace-nowrap">{user.username}</td>
                  <td className="px-6 py-4 whitespace-nowrap">{user.email}</td>
                  <td className="px-6 py-4 whitespace-nowrap">{user.credits}</td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    {new Date(user.createdAt).toLocaleDateString()}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Content Moderation Section */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-semibold mb-4">Content Moderation</h2>
        
        {reportedContent.length > 0 ? (
          <div className="space-y-4">
            {reportedContent.map(content => (
              <div key={content._id} className="border rounded-lg p-4">
                <div className="flex justify-between items-start">
                  <div>
                    <h3 className="font-medium">
                      {content.source.toUpperCase()}: {content.data.title || content.data.text?.substring(0, 50)}
                    </h3>
                    <p className="text-sm text-gray-500 mt-1">
                      Reported by {content.reportedBy.length} users
                    </p>
                  </div>
                  <div className="flex space-x-2">
                    <button
                      onClick={() => handleContentModeration(content._id, 'approve')}
                      className="bg-green-500 text-white px-3 py-1 rounded text-sm hover:bg-green-600"
                    >
                      Approve
                    </button>
                    <button
                      onClick={() => handleContentModeration(content._id, 'remove')}
                      className="bg-red-500 text-white px-3 py-1 rounded text-sm hover:bg-red-600"
                    >
                      Remove
                    </button>
                  </div>
                </div>
                <div className="mt-2 p-2 bg-gray-50 rounded">
                  <pre className="text-xs overflow-x-auto">
                    {JSON.stringify(content.data, null, 2)}
                  </pre>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <p className="text-gray-500">No reported content to moderate</p>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
